// src/components/MapPicker.jsx
import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, useMapEvents } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// Fix para iconos Leaflet en React
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png').default,
  iconUrl: require('leaflet/dist/images/marker-icon.png').default,
  shadowUrl: require('leaflet/dist/images/marker-shadow.png').default,
});

function LocationMarker({ latitude, longitude, setPosition, readOnly }) {
  // Hook para manejar clic en mapa
  useMapEvents({
    click(e) {
      if (!readOnly) {
        setPosition([e.latlng.lat, e.latlng.lng]);
      }
    }
  });

  if (!latitude || !longitude) return null;

  return (
    <Marker position={[latitude, longitude]} />
  );
}

export default function MapPicker({ latitude, longitude, onChange, readOnly = false, zoom = 13, height = 300 }) {
  const [position, setPosition] = useState([latitude, longitude]);

  // Sincronizar estado interno con props externos
  useEffect(() => {
    if (latitude && longitude) {
      setPosition([latitude, longitude]);
    }
  }, [latitude, longitude]);

  // Avisar al padre cuando cambia posición
  useEffect(() => {
    if (onChange && position) {
      onChange(position[0], position[1]);
    }
  }, [position, onChange]);

  return (
    <MapContainer
      center={position || [0, 0]}
      zoom={zoom}
      style={{ height: height, width: '100%' }}
      scrollWheelZoom={!readOnly}
      dragging={!readOnly}
      doubleClickZoom={!readOnly}
      touchZoom={!readOnly}
    >
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
      {position && <LocationMarker latitude={position[0]} longitude={position[1]} setPosition={setPosition} readOnly={readOnly} />}
    </MapContainer>
  );
}
