// src/components/ActivityCard.jsx
import React, { useState } from 'react';
import ActivityMap from './ActivityMap';
import StarRater from './StarRater';

export default function ActivityCard({
  activity,
  onJoin,
  onRate,
  addToast
}) {
  const {
    title,
    user,
    starts_at,
    ends_at,
    location,
    is_registered,
    is_finished,
    participants_count,
    average_rating,
    latitude,
    longitude,
    description
  } = activity;

  const [localAverage, setLocalAverage] = useState(Number(average_rating) || 0); // ✅ Control local del promedio
  const [localCount, setLocalCount] = useState(participants_count || 0); // ✅ Control local del número de participantes

  const handleRate = async (newRating) => {
    if (onRate) {
      await onRate(newRating); // Enviar la nueva valoración al backend
      setLocalAverage((prevAverage) => {
        const total = prevAverage * localCount + newRating;
        const newAverage = (total / (localCount + 1)).toFixed(1);
        setLocalCount(localCount + 1); // ✅ Incrementar el contador local
        return Number(newAverage);
      });
    }
  };

  return (
    <div className="card mb-3">
      <div className="card-body">
        <h5 className="card-title">{title}</h5>
        <p className="text-muted small mb-2">
          Propuesta por: {user?.name || '-'}
        </p>
        {description && <p className="card-text">{description}</p>}
        <p className="mb-1"><strong>Inicio:</strong> {new Date(starts_at).toLocaleString()}</p>
        {ends_at && <p className="mb-2"><strong>Fin:</strong> {new Date(ends_at).toLocaleString()}</p>}

        {location && <p className="mb-2"><strong>Lugar:</strong> {location}</p>}

        {/* ── MAPA ─────────────────────────────────────────── */}
        {latitude && longitude && (
          <div className="mb-3">
            <ActivityMap latitude={latitude} longitude={longitude} />
          </div>
        )}

        <p className="mb-2"><strong>Asistentes:</strong> {localCount}</p>

        {/* ── ESTRELLAS Y PROMEDIO ─────────────────────────── */}
        <div className="mb-3">
          <div className="d-flex align-items-center">
            <StarRater
              activityId={activity.id}
              initialValue={localAverage}
              readOnly={!is_finished}
              onRate={handleRate}
            />
            <span className="ms-2">Promedio: {localAverage} ★</span>
          </div>
        </div>

        {/* ── BOTÓN DE INSCRIPCIÓN ──────────────────────────── */}
        <button
          className={'btn ' + (is_registered ? 'btn-secondary disabled' : 'btn-primary')}
          onClick={onJoin}
          disabled={is_registered}
        >
          {is_registered ? 'Ya inscrito' : 'Apuntarse'}
        </button>
      </div>
    </div>
  );
}
