// src/pages/LostPets.jsx

import React, { useState, useEffect, useMemo, useCallback } from 'react';
import api from '../api/axios';
import { MapContainer, TileLayer, Marker } from 'react-leaflet';
import L from 'leaflet';

// Configure default Leaflet icon URLs
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.3/images/marker-icon-2x.png',
  iconUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.3/images/marker-icon.png',
  shadowUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.3/images/marker-shadow.png',
});

// Haversine formula to compute kilometers between two coords
function distanceKm(lat1, lon1, lat2, lon2) {
  const R = 6371;
  const dLat = ((lat2 - lat1) * Math.PI) / 180;
  const dLon = ((lon2 - lon1) * Math.PI) / 180;
  const a =
    Math.sin(dLat / 2) ** 2 +
    Math.cos((lat1 * Math.PI) / 180) *
      Math.cos((lat2 * Math.PI) / 180) *
      Math.sin(dLon / 2) ** 2;
  return 2 * R * Math.asin(Math.sqrt(a));
}

export default function LostPets({ addToast, user }) {
  // All lost pets
  const [lostPets, setLostPets] = useState([]);

  // Filters state, now including province
  const [filters, setFilters] = useState({
    name: '',
    province: '',
    fromDate: '',
    toDate: '',
    nearMe: false,
    maxKm: 20,
    recentFirst: true,
  });

  // Fetch data
  const fetchLostPets = useCallback(async () => {
    try {
      const { data } = await api.get('/api/lost-pets');
      setLostPets(data);
    } catch {
      addToast('Error cargando registros de pérdidas', 'error');
    }
  }, [addToast]);

  useEffect(() => {
    fetchLostPets();
  }, [fetchLostPets]);

  // Unique provinces from last_seen_location
  const provinces = useMemo(
    () =>
      Array.from(
        new Set(
          lostPets
            .map((p) => p.last_seen_location)
            .filter((loc) => loc && loc.trim() !== '')
        )
      ),
    [lostPets]
  );

  // Apply all filters + ordering
  const filtered = useMemo(() => {
    let arr = lostPets.filter((p) => {
      // name filter
      if (
        filters.name &&
        !p.pet_name.toLowerCase().includes(filters.name.toLowerCase())
      ) {
        return false;
      }
      // province filter
      if (filters.province && p.last_seen_location !== filters.province) {
        return false;
      }
      // date filters
      const dt = new Date(p.posted_at);
      if (filters.fromDate && dt < new Date(filters.fromDate)) return false;
      if (filters.toDate && dt > new Date(filters.toDate)) return false;
      // nearMe filter
      if (filters.nearMe && user.latitude && user.longitude) {
        const km = distanceKm(
          user.latitude,
          user.longitude,
          p.last_seen_latitude,
          p.last_seen_longitude
        );
        if (km > filters.maxKm) return false;
      }
      return true;
    });
    // sort by posted_at
    arr.sort((a, b) => {
      const da = new Date(a.posted_at),
        db = new Date(b.posted_at);
      return filters.recentFirst ? db - da : da - db;
    });
    return arr;
  }, [lostPets, filters, user]);

  return (
    <>
      <h1 className="mb-4">Mascotas Perdidas</h1>

      {/* Filters Panel */}
      <div className="card mb-4 p-3">
        <div className="row g-2">
          <div className="col-md">
            <input
              type="text"
              className="form-control"
              placeholder="Buscar por nombre..."
              value={filters.name}
              onChange={(e) =>
                setFilters((f) => ({ ...f, name: e.target.value }))
              }
            />
          </div>
          <div className="col-md">
            <select
              className="form-select"
              value={filters.province}
              onChange={(e) =>
                setFilters((f) => ({ ...f, province: e.target.value }))
              }
            >
              <option value="">Todas las provincias</option>
              {provinces.map((pr) => (
                <option key={pr} value={pr}>
                  {pr}
                </option>
              ))}
            </select>
          </div>
          <div className="col-md">
            <input
              type="date"
              className="form-control"
              value={filters.fromDate}
              onChange={(e) =>
                setFilters((f) => ({ ...f, fromDate: e.target.value }))
              }
            />
          </div>
          <div className="col-md">
            <input
              type="date"
              className="form-control"
              value={filters.toDate}
              onChange={(e) =>
                setFilters((f) => ({ ...f, toDate: e.target.value }))
              }
            />
          </div>
          <div className="col-auto d-flex align-items-center">
            <div className="form-check me-3">
              <input
                id="nearMe"
                type="checkbox"
                className="form-check-input"
                checked={filters.nearMe}
                onChange={(e) =>
                  setFilters((f) => ({ ...f, nearMe: e.target.checked }))
                }
              />
              <label htmlFor="nearMe" className="form-check-label">
                Cerca de mí ({filters.maxKm} km)
              </label>
            </div>
            <div className="form-check">
              <input
                id="recentFirst"
                type="checkbox"
                className="form-check-input"
                checked={filters.recentFirst}
                onChange={(e) =>
                  setFilters((f) => ({
                    ...f,
                    recentFirst: e.target.checked,
                  }))
                }
              />
              <label htmlFor="recentFirst" className="form-check-label">
                Más recientes primero
              </label>
            </div>
          </div>
        </div>
      </div>

      {/* Cards Grid */}
      <div className="row">
        {filtered.length === 0 && (
          <div className="col-12 text-center text-muted my-5">
            No hay mascotas que coincidan.
          </div>
        )}

        {filtered.map((p) => (
          <div key={p.id} className="col-md-6 col-lg-4 mb-4">
            <div className="card h-100 shadow-sm">
              {p.photo && (
                <img
                  src={p.photo}
                  alt={p.pet_name}
                  className="card-img-top"
                  style={{ height: '200px', objectFit: 'cover' }}
                />
              )}
              <div className="card-body d-flex flex-column">
                <h5 className="card-title">{p.pet_name}</h5>
                {p.description && (
                  <p className="card-text">{p.description}</p>
                )}
                <p className="mb-1">
                  <strong>Ubicación:</strong> {p.last_seen_location}
                </p>
                <p className="text-muted mb-3">
                  Reportado: {new Date(p.posted_at).toLocaleDateString()}
                </p>

                {/* only render static map if we have lat & lng */}
                {p.last_seen_latitude != null &&
                  p.last_seen_longitude != null && (
                    <div style={{ height: '150px', marginBottom: '1rem' }}>
                      <MapContainer
                        center={[
                          p.last_seen_latitude,
                          p.last_seen_longitude,
                        ]}
                        zoom={13}
                        dragging={false}
                        doubleClickZoom={false}
                        scrollWheelZoom={false}
                        zoomControl={false}
                        style={{ height: '100%', width: '100%' }}
                      >
                        <TileLayer url="https://tile.openstreetmap.org/{z}/{x}/{y}.png" />
                        <Marker
                          position={[
                            p.last_seen_latitude,
                            p.last_seen_longitude,
                          ]}
                        />
                      </MapContainer>
                    </div>
                  )}

                <div className="mt-auto">
                  <button className="btn btn-primary btn-sm w-100">
                    Ver detalles
                  </button>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </>
  );
}
