import React, { useState } from 'react';
import api from '../api/axios';

export default function PetCard({ pet, onDelete, onEdit }) {
  const [isWalking, setIsWalking] = useState(pet.is_walking);
  const [loading, setLoading] = useState(false);

  const toggleWalk = async () => {
    try {
      setLoading(true);
      await api.patch(`/api/pets/${pet.id}/toggle-walk`);
      setIsWalking(prev => !prev);
    } catch (err) {
      console.error(err);
      alert('Error al actualizar estado de paseo.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="card h-100">
      {pet.photo_url && (
        <img src={pet.photo_url} className="card-img-top" alt={pet.name} />
      )}
      <div className="card-body">
        <h5 className="card-title">{pet.name}</h5>
        <p className="card-text">
          <strong>Raza:</strong> {pet.breed || '—'}<br />
          <strong>Edad:</strong> {pet.age ?? '—'} años<br />
          <strong>Estado:</strong> {isWalking ? 'De paseo' : 'En casa'}
        </p>
        <button
          className={`btn ${isWalking ? 'btn-warning' : 'btn-outline-success'} btn-sm me-2`}
          onClick={toggleWalk}
          disabled={loading}
        >
          {isWalking ? 'Marcar como en casa' : 'Marcar como de paseo'}
        </button>
        <button className="btn btn-primary btn-sm me-2" onClick={onEdit}>Editar</button>
        <button className="btn btn-danger btn-sm" onClick={onDelete}>Eliminar</button>
      </div>
    </div>
  );
}
