// src/api/axios.js
import axios from 'axios';

export default axios.create({
  baseURL: 'http://127.0.0.1:8000',  // tu backend Laravel
  withCredentials: true,              // muy importante para cookies de Sanctum
  headers: { 'Content-Type': 'application/json' }
});
