// src/components/ActivityCard.jsx
import React from 'react';
import StarRater from './StarRater';

const ActivityCard = ({ activity, onJoin, addToast, onRate }) => {
  const handleRate = (rating) => {
    onRate(activity.id, rating);
  };

  return (
    <div className="card mb-3">
      <div className="card-body">
        <h5 className="card-title">{activity.title}</h5>
        <p className="card-text">{activity.description}</p>
        <p className="card-text">
          <small className="text-muted">
            {new Date(activity.starts_at).toLocaleString()} - {new Date(activity.ends_at).toLocaleString()}
          </small>
        </p>
        <p className="card-text">
          <strong>Ubicaci√≥n:</strong> {activity.location}
        </p>
        <p className="card-text">
          <strong>Asistentes:</strong> {activity.participants_count}
        </p>
        {activity.is_finished && (
          <StarRater
            activityId={activity.id}
            initialValue={activity.average_rating}
            readOnly={false}
            onRate={handleRate}
          />
        )}
        {!activity.is_registered && (
          <button className="btn btn-primary mt-2" onClick={onJoin}>
            Inscribirse
          </button>
        )}
      </div>
    </div>
  );
};

export default ActivityCard;
