import React, { useState, useEffect, useMemo, useCallback, Fragment } from 'react';
import { Link } from 'react-router-dom';
import { MapContainer, TileLayer, Marker } from 'react-leaflet';
import L from 'leaflet';
import api from '../api/axios';

/* -------------- Leaflet default icon -------------- */
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.3/images/marker-icon-2x.png',
  iconUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.3/images/marker-icon.png',
  shadowUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.3/images/marker-shadow.png',
});

/* -------------- Haversine util (km) -------------- */
function distanceKm(lat1, lon1, lat2, lon2) {
  const R = 6371;
  const dLat = ((lat2 - lat1) * Math.PI) / 180;
  const dLon = ((lon1 - lon2) * Math.PI) / 180;
  const a =
    Math.sin(dLat / 2) ** 2 +
    Math.cos((lat1 * Math.PI) / 180) *
      Math.cos((lat2 * Math.PI) / 180) *
      Math.sin(dLon / 2) ** 2;
  return 2 * R * Math.asin(Math.sqrt(a));
}

/* -------------- Component -------------- */
export default function LostPets({ addToast, user }) {
  const [lostPets, setLostPets] = useState([]);
  const [expandedId, setExpandedId] = useState(null);

  /* ---------- filters ---------- */
  const [filters, setFilters] = useState({
    name: '',
    province: '',
    fromDate: '',
    toDate: '',
    nearMe: false,
    maxKm: 20,
    recentFirst: true,
  });

  /* ---------- fetch ---------- */
  const fetchLostPets = useCallback(async () => {
    try {
      const res = await api.get('/api/lost-pets');
      setLostPets(res.data.data ?? res.data);
    } catch {
      addToast('Error cargando pérdidas', 'error');
    }
  }, [addToast]);

  useEffect(() => {
    fetchLostPets();
  }, [fetchLostPets]);

  /* ---------- provincias únicas ---------- */
  const provinces = useMemo(
    () =>
      Array.from(
        new Set(
          lostPets
            .map(p => p.location ?? '')
            .filter(loc => loc.trim() !== '')
        )
      ),
    [lostPets]
  );

  /* ---------- aplicar filtros + sort ---------- */
  const filtered = useMemo(() => {
    let arr = lostPets.filter(p => {
      // Verificar si tiene nombre
      if (filters.name && p.pet?.name && !p.pet.name.toLowerCase().includes(filters.name.toLowerCase())) {
        return false;
      }

      // Verificar provincia
      if (filters.province && p.location !== filters.province) {
        return false;
      }

      // Verificar fechas
      const dt = new Date(p.posted_at);
      if (filters.fromDate && dt < new Date(filters.fromDate)) return false;
      if (filters.toDate && dt > new Date(filters.toDate)) return false;

      // Verificar cercanía
      if (filters.nearMe && user?.latitude && user?.longitude) {
        if (p.lat && p.lng) {
          const km = distanceKm(
            user.latitude,
            user.longitude,
            p.lat,
            p.lng
          );
          if (km > filters.maxKm) return false;
        }
      }

      return true;
    });

    // Ordenar por fecha
    arr.sort((a, b) => {
      const da = new Date(a.posted_at),
        db = new Date(b.posted_at);
      return filters.recentFirst ? db - da : da - db;
    });

    return arr;
  }, [lostPets, filters, user]);

  const toggleDetails = id =>
    setExpandedId(expandedId === id ? null : id);

  /* ---------- render ---------- */
  return (
    <Fragment>
      <h1 className="mb-4">Mascotas Perdidas</h1>

      {/* -------- panel de filtros -------- */}
      <div className="card mb-4 p-3">
        <input
          type="text"
          className="form-control mb-2"
          placeholder="Buscar por nombre..."
          value={filters.name}
          onChange={e =>
            setFilters(f => ({ ...f, name: e.target.value }))
          }
        />
      </div>

      <div className="row">
        {filtered.length === 0 ? (
          <div className="col-12 text-center text-muted my-5">
            No hay mascotas que coincidan.
          </div>
        ) : (
          filtered.map(p => (
            <div key={p.id} className="col-md-6 col-lg-4 mb-4">
              <div className="card h-100 shadow-sm">
                <img
                  src={p.photo ?? 'https://via.placeholder.com/300x200'}
                  alt={p.pet?.name ?? 'Mascota Perdida'}
                  className="card-img-top"
                  style={{ height: 200, objectFit: 'cover' }}
                />

                <div className="card-body">
                  <h5 className="card-title">{p.pet?.name ?? 'Mascota Desconocida'}</h5>
                  <p>{p.comment ?? 'Sin descripción'}</p>
                  <p><strong>Ubicación:</strong> {p.location ?? 'Desconocida'}</p>
                  <p><strong>Reportado:</strong> {new Date(p.posted_at).toLocaleDateString()}</p>

                  <button
                    className="btn btn-primary btn-sm mt-2"
                    onClick={() => toggleDetails(p.id)}
                  >
                    {expandedId === p.id ? 'Ocultar detalles' : 'Ver detalles'}
                  </button>

                  {expandedId === p.id && p.lat && p.lng && (
                    <MapContainer
                      center={[p.lat, p.lng]}
                      zoom={15}
                      style={{ height: 200, width: '100%' }}
                    >
                      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
                      <Marker position={[p.lat, p.lng]} />
                    </MapContainer>
                  )}
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </Fragment>
  );
}
