// src/pages/LostReportForm.jsx

import React, { useState, useEffect, useCallback } from 'react';
import api from '../api/axios';
import { MapContainer, TileLayer, Marker, useMapEvents } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// Corregir iconos default Leaflet en React (si no lo tienes ya)
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png').default,
  iconUrl: require('leaflet/dist/images/marker-icon.png').default,
  shadowUrl: require('leaflet/dist/images/marker-shadow.png').default,
});

// Componente para seleccionar ubicaci贸n en el mapa
function LocationPicker({ latitude, longitude, onChange }) {
  useMapEvents({
    click(e) {
      onChange(e.latlng.lat, e.latlng.lng);
    }
  });

  return latitude && longitude ? (
    <Marker position={[latitude, longitude]} />
  ) : null;
}

export default function LostReportForm({ addToast, user }) {
  const [myPets, setMyPets] = useState([]);
  const [form, setForm] = useState({
    type: 'lost', // 'lost' | 'found'
    pet_id: '',
    pet_name: '',
    comment: '',
    happened_at: '',
    latitude: null,
    longitude: null,
    photo: null,
  });

  // Cargar mascotas del usuario
  const fetchMyPets = useCallback(async () => {
    try {
      const res = await api.get('/api/pets');
      setMyPets(res.data);
    } catch {
      addToast('Error cargando mascotas', 'error');
    }
  }, [addToast]);

  useEffect(() => {
    fetchMyPets();
  }, [fetchMyPets]);

  // Cambios en el formulario
  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setForm((f) => ({
      ...f,
      [name]: name === 'photo' ? files[0] : value,
    }));
  };

  const handleMapChange = (lat, lng) => {
    setForm((f) => ({ ...f, latitude: lat, longitude: lng }));
  };

  // Enviar formulario
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const fd = new FormData();
      Object.keys(form).forEach((key) => {
        if (form[key]) fd.append(key, form[key]);
      });
      await api.post('/api/lost-reports', fd);
      addToast('Reporte enviado', 'success');
      setForm({
        type: 'lost',
        pet_id: '',
        pet_name: '',
        comment: '',
        happened_at: '',
        latitude: null,
        longitude: null,
        photo: null,
      });
    } catch {
      addToast('Error al enviar el reporte', 'error');
    }
  };

  return (
    <div>
      <h1>Reportar Mascota Perdida o Encontrada</h1>

      <form onSubmit={handleSubmit} className="card p-4 mb-4">
        <select
          name="type"
          value={form.type}
          onChange={handleChange}
          className="form-select mb-3"
        >
          <option value="lost">Reportar Mascota Perdida</option>
          <option value="found">Reportar Mascota Encontrada</option>
        </select>

        {form.type === 'lost' ? (
          <select
            name="pet_id"
            value={form.pet_id}
            onChange={handleChange}
            className="form-select mb-3"
            required
          >
            <option value="">Selecciona tu mascota</option>
            {myPets.map((pet) => (
              <option key={pet.id} value={pet.id}>
                {pet.name}
              </option>
            ))}
          </select>
        ) : (
          <input
            name="pet_name"
            className="form-control mb-3"
            placeholder="Nombre de la mascota encontrada"
            value={form.pet_name}
            onChange={handleChange}
            required
          />
        )}

        <textarea
          name="comment"
          className="form-control mb-3"
          placeholder="Descripci贸n del reporte"
          value={form.comment}
          onChange={handleChange}
          required
        />

        <input
          type="datetime-local"
          name="happened_at"
          className="form-control mb-3"
          value={form.happened_at}
          onChange={handleChange}
          required
        />

        {/* MAPA para seleccionar ubicaci贸n */}
        <div style={{ height: 300, marginBottom: 20 }}>
          <MapContainer
            center={
              form.latitude && form.longitude
                ? [form.latitude, form.longitude]
                : [40.4168, -3.7038] // Madrid como default
            }
            zoom={form.latitude && form.longitude ? 14 : 5}
            style={{ height: '100%', width: '100%' }}
          >
            <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
            <LocationPicker
              latitude={form.latitude}
              longitude={form.longitude}
              onChange={handleMapChange}
            />
          </MapContainer>
        </div>

        <div className="mt-3 mb-3">
          <input
            type="file"
            name="photo"
            className="form-control"
            onChange={handleChange}
          />
        </div>

        <button className="btn btn-danger">Reportar</button>
      </form>
    </div>
  );
}
