// src/pages/LostPets.jsx
import React, { useState, useEffect, useMemo, useCallback, Fragment } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import api from '../api/axios';
import 'leaflet/dist/leaflet.css';

// Configuración de icono personalizado para Leaflet
const customMarker = new L.Icon({
  iconUrl: require('leaflet/dist/images/marker-icon.png').default,
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png').default,
  shadowUrl: require('leaflet/dist/images/marker-shadow.png').default,
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowSize: [41, 41],
});

export default function LostPets({ addToast, user }) {
  const [lostPets, setLostPets] = useState([]); // ✅ Inicializamos como array vacío
  const [tab, setTab] = useState('lost'); // 'lost' | 'found'
  const [expandedId, setExpandedId] = useState(null);
  const [comments, setComments] = useState({});
  const [newComment, setNewComment] = useState('');

  // ✅ Cargar mascotas perdidas
  const fetchLostPets = useCallback(async () => {
    try {
      const res = await api.get('/api/lost-pets');
      if (Array.isArray(res.data)) {
        setLostPets(res.data);
      } else {
        setLostPets([]); // En caso de que no sea un array
      }
    } catch {
      addToast('Error cargando mascotas perdidas', 'error');
      setLostPets([]); // En caso de error
    }
  }, [addToast]);

  useEffect(() => {
    fetchLostPets();
  }, [fetchLostPets]);

  // ✅ Cargar comentarios
  const fetchComments = async (petId) => {
    try {
      const res = await api.get(`/api/lost-pets/${petId}/comments`);
      setComments((prev) => ({ ...prev, [petId]: res.data }));
    } catch {
      addToast('Error cargando comentarios', 'error');
    }
  };

  // ✅ Agregar comentario
  const addComment = async (petId) => {
    if (!newComment.trim()) return;

    try {
      await api.post(`/api/lost-pets/${petId}/comments`, { body: newComment });
      setNewComment('');
      fetchComments(petId);
    } catch {
      addToast('Error al agregar comentario', 'error');
    }
  };

  // ✅ Marcar como encontrada (solo dueño)
  const markAsFound = async (petId) => {
    try {
      await api.post(`/api/lost-pets/${petId}/mark-as-found`);
      addToast('Mascota marcada como encontrada', 'success');
      fetchLostPets();
    } catch {
      addToast('Error al marcar como encontrada', 'error');
    }
  };

  // ✅ Mostrar detalles
  const toggleDetails = (id) => {
    if (expandedId === id) {
      setExpandedId(null);
    } else {
      setExpandedId(id);
      fetchComments(id);
    }
  };

  // ✅ Filtrar mascotas por tipo (perdidas o encontradas)
  const filtered = useMemo(() => {
    return Array.isArray(lostPets) ? lostPets.filter(p => (tab === 'lost' ? !p.found : p.found)) : [];
  }, [lostPets, tab]);

  return (
    <div className="container mt-4">
      <h1>Mascotas Perdidas</h1>

      {/* Tabs */}
      <div className="mb-3">
        <button
          className={`btn ${tab === 'lost' ? 'btn-primary' : 'btn-outline-primary'}`}
          onClick={() => setTab('lost')}
        >
          Mascotas Perdidas
        </button>
        <button
          className={`btn ms-2 ${tab === 'found' ? 'btn-primary' : 'btn-outline-primary'}`}
          onClick={() => setTab('found')}
        >
          Mascotas Encontradas
        </button>
      </div>

      {/* Lista de mascotas */}
      <div className="row">
        {filtered.length === 0 ? (
          <div className="col-12 text-center text-muted my-5">
            No hay mascotas en esta categoría.
          </div>
        ) : (
          filtered.map((p) => (
            <div key={p.id} className="col-md-6 mb-4">
              <div className="card">
                {p.photo && (
                  <img src={p.photo} alt={p.pet_name} className="card-img-top" style={{ height: 200, objectFit: 'cover' }} />
                )}
                <div className="card-body">
                  <h5 className="card-title">{p.pet_name || 'Mascota Desconocida'}</h5>
                  <p>{p.description}</p>
                  <p><strong>Ubicación:</strong> {p.last_seen_location}</p>
                  <button className="btn btn-sm btn-info mt-2" onClick={() => toggleDetails(p.id)}>
                    {expandedId === p.id ? 'Ocultar detalles' : 'Ver detalles'}
                  </button>

                  {expandedId === p.id && (
                    <div className="mt-3">
                      {/* Mapa con ubicación */}
                      {p.latitude && p.longitude && (
                        <MapContainer center={[p.latitude, p.longitude]} zoom={15} style={{ height: '200px', width: '100%' }}>
                          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
                          <Marker position={[p.latitude, p.longitude]} icon={customMarker}>
                            <Popup>{p.pet_name || 'Mascota Perdida'}</Popup>
                          </Marker>
                        </MapContainer>
                      )}

                      {/* Comentarios */}
                      <div className="mt-3">
                        <h6>Comentarios:</h6>
                        <ul className="list-group mb-2">
                          {comments[p.id]?.length ? (
                            comments[p.id].map((comment) => (
                              <li key={comment.id} className="list-group-item">
                                <strong>{comment.user?.name || 'Anónimo'}:</strong> {comment.body}
                              </li>
                            ))
                          ) : (
                            <li className="list-group-item text-muted">Sin comentarios.</li>
                          )}
                        </ul>
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
