import React, { useEffect } from 'react';

export default function Toast({ id, message, bg, onClose, ms = 4000 }) {
  /* autocierre */
  useEffect(() => {
    const t = setTimeout(() => onClose(id), ms);
    return () => clearTimeout(t);
  }, [id, ms, onClose]);

  return (
    <div
      className="toast show text-white mb-2"
      style={{ background: bg, minWidth: 250 }}
    >
      <div className="d-flex align-items-center">
        <div className="toast-body pe-2">{message}</div>

        {/* ---------- botón de cierre ---------- */}
        <button
          type="button"          /* ← evita submit por defecto */
          className="btn-close btn-close-white me-2 m-auto"
          aria-label="Close"
          onClick={() => onClose(id)}
        />
      </div>
    </div>
  );
}
