import { useEffect, useState } from 'react';
import api from '../../api/axios';

export default function CommentAdmin() {
  const [activityComments, setActivityComments] = useState([]);
  const [lostPetComments, setLostPetComments] = useState([]);
  const [lostReportComments, setLostReportComments] = useState([]);

  useEffect(() => {
    loadComments();
  }, []);

  const loadComments = async () => {
    try {
      const { data } = await api.get('/api/admin/comments');

      setActivityComments(data.activityComments || []);
      setLostPetComments(data.lostPetComments || []);
      setLostReportComments(data.lostReportComments || []);
    } catch (err) {
      console.error('Error cargando comentarios:', err);
      setActivityComments([]);
      setLostPetComments([]);
      setLostReportComments([]);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('¿Eliminar comentario?')) return;
    try {
      await api.delete(`/api/admin/comments/${id}`);
      loadComments(); // Recargar tras eliminar
    } catch (err) {
      console.error('Error al eliminar comentario:', err);
    }
  };

  const renderList = (title, comments) => (
    <>
      <h4 className="mt-4">{title}</h4>
      <ul className="list-group">
        {comments.length === 0 && <li className="list-group-item text-muted">No hay comentarios.</li>}
        {comments.map(c => (
          <li key={c.id} className="list-group-item d-flex justify-content-between align-items-center">
            {c.body}
            <button className="btn btn-sm btn-danger" onClick={() => handleDelete(c.id)}>Eliminar</button>
          </li>
        ))}
      </ul>
    </>
  );

  return (
    <div>
      <h2>Gestión de Comentarios</h2>
      {renderList("Comentarios en Actividades", activityComments)}
      {renderList("Comentarios en Mascotas Perdidas", lostPetComments)}
      {renderList("Comentarios en Reportes de Pérdida", lostReportComments)}
    </div>
  );
}
