// src/pages/LostReports.jsx

import React, { useState, useEffect, useCallback } from 'react';
import api from '../api/axios';
import MapPicker from '../components/MapPicker';

export default function LostReports({ addToast, user }) {
  const [reports, setReports] = useState([]);
  const [myPets, setMyPets] = useState([]);
  const [form, setForm] = useState({
    type: 'lost',     // 'lost' | 'found'
    pet_id: '',       // para lost
    pet_name: '',     // para found
    comment: '',
    happened_at: '',
    latitude: null,
    longitude: null,
    photo: null,
  });

  // Carga inicial de reportes y mascotas del usuario
  const fetchData = useCallback(async () => {
    try {
      const [reportsRes, petsRes] = await Promise.all([
        api.get('/api/lost-reports'),
        api.get('/api/pets')
      ]);
      setReports(reportsRes.data);
      setMyPets(petsRes.data);
    } catch {
      addToast('Error cargando reportes o mascotas', 'error');
    }
  }, [addToast]);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  // Manejar cambios de inputs (texto, selects, files)
  const handleChange = e => {
    const { name, value, files } = e.target;
    setForm(f => ({
      ...f,
      [name]: name === 'photo' ? files[0] : value
    }));
  };

  // Manejar clic en el mapa para seleccionar ubicación
  const handleMap = (lat, lng) => {
    setForm(f => ({ ...f, latitude: lat, longitude: lng }));
  };

  // Envío del formulario
  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const fd = new FormData();
      // Añadimos sólo campos con valor
      Object.entries(form).forEach(([key, val]) => {
        if (val !== null && val !== '') {
          // El backend espera pet_id o pet_name según type
          fd.append(key, val);
        }
      });
      await api.post('/api/lost-reports', fd, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      addToast('Reporte enviado', 'success');
      setForm({
        type: 'lost',
        pet_id: '',
        pet_name: '',
        comment: '',
        happened_at: '',
        latitude: null,
        longitude: null,
        photo: null,
      });
      fetchData();
    } catch (err) {
      console.error(err.response || err);
      addToast('Error al enviar el reporte', 'error');
    }
  };

  return (
    <div>
      <h1 className="mb-4">Reportar Mascota Perdida o Encontrada</h1>

      {/* Formulario de reporte */}
      <form onSubmit={handleSubmit} className="card p-4 mb-5">
        <div className="row g-2">
          <div className="col-md-4">
            <label className="form-label">Tipo de reporte</label>
            <select
              name="type"
              value={form.type}
              onChange={handleChange}
              className="form-select"
            >
              <option value="lost">Mascota Perdida</option>
              <option value="found">Mascota Encontrada</option>
            </select>
          </div>

          {form.type === 'lost' ? (
            <div className="col-md-8">
              <label className="form-label">Selecciona tu mascota</label>
              <select
                name="pet_id"
                value={form.pet_id}
                onChange={handleChange}
                className="form-select"
                required
              >
                <option value="">-- Elige mascota --</option>
                {myPets.map(p => (
                  <option key={p.id} value={p.id}>
                    {p.name}
                  </option>
                ))}
              </select>
            </div>
          ) : (
            <div className="col-md-8">
              <label className="form-label">Nombre de la mascota encontrada</label>
              <input
                type="text"
                name="pet_name"
                value={form.pet_name}
                onChange={handleChange}
                className="form-control"
                placeholder="Ej. «Perro callejero»"
                required
              />
            </div>
          )}
        </div>

        <div className="mb-3 mt-3">
          <label className="form-label">Descripción / Comentario</label>
          <textarea
            name="comment"
            value={form.comment}
            onChange={handleChange}
            className="form-control"
            placeholder="Detalles adicionales..."
            required
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Fecha y hora</label>
          <input
            type="datetime-local"
            name="happened_at"
            value={form.happened_at}
            onChange={handleChange}
            className="form-control"
            required
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Ubicación (haz clic en el mapa)</label>
          <MapPicker
            latitude={form.latitude}
            longitude={form.longitude}
            onChange={handleMap}
            readOnly={false}
            height={300}
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Foto (opcional)</label>
          <input
            type="file"
            name="photo"
            accept="image/*"
            onChange={handleChange}
            className="form-control"
          />
        </div>

        <button type="submit" className="btn btn-danger">
          Reportar
        </button>
      </form>

      {/* Listado de reportes */}
      <h2 className="mb-4">Reportes</h2>
      <div className="row">
        {reports.map(report => (
          <div key={report.id} className="col-md-6 mb-4">
            <div className="card h-100">
              {report.photo && (
                <img
                  src={report.photo}
                  alt="Foto del reporte"
                  className="card-img-top"
                  style={{ height: 200, objectFit: 'cover' }}
                />
              )}
              <div className="card-body d-flex flex-column">
                <h5 className="card-title">
                  {report.type === 'lost'
                    ? report.pet?.name || 'Mascota Perdida'
                    : report.pet_name || 'Mascota Encontrada'}
                </h5>
                <p className="card-text">{report.comment}</p>
                <p className="text-muted mb-2">
                  <strong>Fecha:</strong>{' '}
                  {new Date(report.happened_at).toLocaleString()}
                </p>

                {report.latitude && report.longitude && (
                  <div style={{ height: 200, marginBottom: '1rem' }}>
                    <MapPicker
                      latitude={report.latitude}
                      longitude={report.longitude}
                      readOnly={true}
                      height={200}
                    />
                  </div>
                )}

                <p className="mt-auto mb-2">
                  <strong>Reportado por:</strong> {report.user?.name || 'Desconocido'}
                </p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
