// PETCONNECT-FRONTEND/src/pages/Activities.jsx
import React, { useState, useEffect } from 'react';
import api from '../api/axios';
import ActivityCard from '../components/ActivityCard';

export default function Activities() {
  const [activities, setActivities] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // 1) Al montar, cargamos actividades y sus comentarios
  useEffect(() => {
    const fetchAll = async () => {
      try {
        const res = await api.get('/api/activities');
        const acts = res.data.map(a => ({
          ...a,
          newComment: '',
          comments: []
        }));
        setActivities(acts);

        // Para cada actividad, traemos sus comentarios
        await Promise.all(
          acts.map(async a => {
            const cRes = await api.get('/api/comments', {
              params: { activity_id: a.id }
            });
            setActivities(cur =>
              cur.map(x => (x.id === a.id ? { ...x, comments: cRes.data } : x))
            );
          })
        );
      } catch (err) {
        console.error(err);
        setError('No se pudieron cargar las actividades.');
      } finally {
        setLoading(false);
      }
    };

    fetchAll();
  }, []);

  // 2) Apuntarse a una actividad
  const handleJoin = async id => {
    try {
      await api.post(`/api/activities/${id}/register`);
      setActivities(cur =>
        cur.map(a => (a.id === id ? { ...a, joined: true } : a))
      );
    } catch (err) {
      console.error(err);
      alert('Error al apuntarse.');
    }
  };

  // 3) Cambiar texto de nuevo comentario
  const handleCommentChange = (id, text) => {
    setActivities(cur =>
      cur.map(a => (a.id === id ? { ...a, newComment: text } : a))
    );
  };

  // 4) Enviar comentario
  const handleCommentSubmit = async id => {
    const act = activities.find(a => a.id === id);
    if (!act.newComment.trim()) return;
    try {
      const res = await api.post('/api/comments', {
        activity_id: id,
        body: act.newComment
      });
      setActivities(cur =>
        cur.map(a =>
          a.id === id
            ? { ...a, comments: [...a.comments, res.data], newComment: '' }
            : a
        )
      );
    } catch (err) {
      console.error(err);
      alert('Error al enviar el comentario.');
    }
  };

  if (loading) return <p>Cargando actividades...</p>;

  return (
    <div>
      <h2 className="mb-4">Actividades Colectivas</h2>
      {error && <div className="alert alert-danger">{error}</div>}
      <div className="row">
        {activities.map(act => (
          <div className="col-md-6 mb-3" key={act.id}>
            <ActivityCard
              activity={act}
              onJoin={handleJoin}
              onCommentChange={handleCommentChange}
              onCommentSubmit={handleCommentSubmit}
            />
          </div>
        ))}
      </div>
    </div>
  );
}
