import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import api from '../api/axios';

export default function Login({ addToast, onLogin }) {
  const [form, setForm] = useState({ email: '', password: '' });
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  /* ---------- handlers ---------- */
  const handleChange = (e) =>
    setForm((f) => ({ ...f, [e.target.name]: e.target.value }));

  const handleSubmit = async (e) => {
  e.preventDefault();
  setLoading(true);
  try {
    const { data } = await api.post('/api/login', form);

    // Guardamos el token y el usuario
    localStorage.setItem('token', data.token);
    localStorage.setItem('user', JSON.stringify(data.user));
    onLogin(data.token);

    addToast('Inicio de sesión exitoso', 'success');

    // Redirigimos según el rol
    if (data.user.role === 'admin') {
      navigate('/admin', { replace: true });
    } else {
      navigate('/pets', { replace: true });
    }
  } catch (err) {
    if (err.response?.status === 401) {
      addToast('Correo o contraseña incorrectos', 'error');
    } else {
      addToast('Error al iniciar sesión', 'error');
    }
  } finally {
    setLoading(false);
  }
};


  /* ---------- render ---------- */
  return (
    <div className="page-auth">
      <h2 className="mb-4 text-center">Iniciar Sesión</h2>

      <form onSubmit={handleSubmit} style={{ maxWidth: 400 }} className="mx-auto">
        <input
          name="email"
          type="email"
          className="form-control mb-2"
          placeholder="Email"
          value={form.email}
          onChange={handleChange}
          required
        />

        <input
          name="password"
          type="password"
          className="form-control mb-3"
          placeholder="Contraseña"
          value={form.password}
          onChange={handleChange}
          required
        />

        <button className="btn btn-primary w-100" disabled={loading}>
          {loading ? 'Ingresando…' : 'Ingresar'}
        </button>
      </form>

      <p className="mt-3 text-center">
        ¿No tienes cuenta?{' '}
        <Link to="/register" className="btn btn-link p-0">
          Regístrate
        </Link>
      </p>
    </div>
  );
}
