// src/pages/LostPets.jsx

import React, { useState, useEffect, useMemo, useCallback, Fragment } from 'react';
import api from '../api/axios';
import MapPicker from '../components/MapPicker';

export default function LostPets({ addToast, user }) {
  const [lostPets, setLostPets] = useState([]);
  const [tab, setTab] = useState('lost'); // 'lost' | 'found'
  const [expandedId, setExpandedId] = useState(null);
  const [comments, setComments] = useState({});
  const [newComment, setNewComment] = useState('');

  // Fetch lost pets
  const fetchLostPets = useCallback(async () => {
    try {
      const res = await api.get('/api/lost-pets');
      setLostPets(res.data);
    } catch {
      addToast('Error cargando mascotas perdidas', 'error');
    }
  }, [addToast]);

  useEffect(() => {
    fetchLostPets();
  }, [fetchLostPets]);

  // Fetch comments for a pet
  const fetchComments = async (petId) => {
    try {
      const res = await api.get(`/api/lost-pets/${petId}/comments`);
      setComments((prev) => ({ ...prev, [petId]: res.data }));
    } catch {
      addToast('Error cargando comentarios', 'error');
    }
  };

  // Add comment
  const addComment = async (petId) => {
    if (!newComment.trim()) return;

    try {
      await api.post(`/api/lost-pets/${petId}/comments`, { body: newComment });
      setNewComment('');
      fetchComments(petId);
    } catch {
      addToast('Error al agregar comentario', 'error');
    }
  };

  // Mark as found (only owner)
  const markAsFound = async (petId) => {
    try {
      await api.post(`/api/lost-pets/${petId}/mark-as-found`);
      addToast('Mascota marcada como encontrada', 'success');
      fetchLostPets();
      setExpandedId(null);
    } catch {
      addToast('Error al marcar como encontrada', 'error');
    }
  };

  // Toggle details
  const toggleDetails = (id) => {
    if (expandedId === id) {
      setExpandedId(null);
    } else {
      setExpandedId(id);
      fetchComments(id);
    }
  };

  // Filter lost or found pets
  const filtered = useMemo(() => {
    return lostPets.filter(p => (tab === 'lost' ? !p.found : p.found));
  }, [lostPets, tab]);

  return (
    <Fragment>
      <h1 className="mb-4">Mascotas Perdidas</h1>

      {/* Tabs */}
      <div className="mb-3">
        <button
          className={`btn ${tab === 'lost' ? 'btn-primary' : 'btn-outline-primary'}`}
          onClick={() => setTab('lost')}
        >
          Mascotas Perdidas
        </button>
        <button
          className={`btn ms-2 ${tab === 'found' ? 'btn-primary' : 'btn-outline-primary'}`}
          onClick={() => setTab('found')}
        >
          Mascotas Encontradas
        </button>
      </div>

      {/* List */}
      <div className="row">
        {filtered.length === 0 ? (
          <div className="col-12 text-center text-muted my-5">
            No hay mascotas en esta categoría.
          </div>
        ) : (
          filtered.map((p) => (
            <div key={p.id} className="col-md-6 mb-4">
              <div className="card">
                {p.photo && (
                  <img
                    src={p.photo}
                    alt={p.pet_name || 'Mascota Perdida'}
                    className="card-img-top"
                    style={{ height: 200, objectFit: 'cover' }}
                  />
                )}
                <div className="card-body">
                  <h5 className="card-title">{p.pet_name || 'Mascota Desconocida'}</h5>
                  <p>{p.description}</p>
                  {p.last_seen_location && (
                    <p>
                      <strong>Ubicación:</strong> {p.last_seen_location}
                    </p>
                  )}
                  <button
                    className="btn btn-sm btn-info mt-2"
                    onClick={() => toggleDetails(p.id)}
                  >
                    {expandedId === p.id ? 'Ocultar detalles' : 'Ver detalles'}
                  </button>

                  {expandedId === p.id && (
                    <div className="mt-3">
                      {p.last_seen_latitude && p.last_seen_longitude && (
                        <MapPicker
                          latitude={p.last_seen_latitude}
                          longitude={p.last_seen_longitude}
                          readOnly={true}
                          height={200}
                        />
                      )}

                      {/* Comments */}
                      <div className="mt-3">
                        <h6>Comentarios:</h6>
                        <ul className="list-group mb-2">
                          {comments[p.id]?.length ? (
                            comments[p.id].map((comment) => (
                              <li key={comment.id} className="list-group-item">
                                <strong>{comment.user?.name || 'Anónimo'}:</strong> {comment.body}
                              </li>
                            ))
                          ) : (
                            <li className="list-group-item text-muted">Sin comentarios.</li>
                          )}
                        </ul>

                        <div className="input-group">
                          <input
                            type="text"
                            className="form-control"
                            placeholder="Escribe un comentario..."
                            value={newComment}
                            onChange={(e) => setNewComment(e.target.value)}
                          />
                          <button
                            className="btn btn-primary"
                            onClick={() => addComment(p.id)}
                          >
                            Enviar
                          </button>
                        </div>

                        {/* Mark as found button - only creator */}
                        {user && user.id === p.user.id && !p.found && (
                          <button
                            className="btn btn-success mt-3"
                            onClick={() => markAsFound(p.id)}
                          >
                            Marcar como encontrada
                          </button>
                        )}
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </Fragment>
  );
}
