// src/components/ActivityCard.jsx
import React from 'react';
import StarRater from './StarRater';

const ActivityCard = ({ activity, onJoin, addToast, onRate }) => {
  const handleRate = (rating) => {
    onRate(activity.id, rating);
  };

  return (
    <div className="card mb-3 shadow-sm p-3">
      <h5 className="card-title">{activity.title}</h5>
      <p className="card-text">{activity.description}</p>
      <p className="text-muted">
        <strong>Fecha:</strong> {new Date(activity.starts_at).toLocaleString()} - {new Date(activity.ends_at).toLocaleString()}
      </p>
      <p><strong>Ubicación:</strong> {activity.location}</p>
      <p><strong>Asistentes:</strong> {activity.participants_count || 0}</p>

      {/* Sistema de estrellas si la actividad está finalizada */}
      {activity.is_finished ? (
        <StarRater
          activityId={activity.id}
          initialValue={activity.average_rating || 0}
          readOnly={false}
          onRate={handleRate}
        />
      ) : (
        <p className="text-muted">La actividad aún no ha finalizado.</p>
      )}
    </div>
  );
};

export default ActivityCard;
