// src/pages/LostReports.jsx

import React, { useState, useEffect, useCallback } from 'react';
import api from '../api/axios';
import MapPicker from '../components/MapPicker';

export default function LostReports({ addToast, user }) {
  const [reports, setReports] = useState([]);    // siempre array
  const [myPets, setMyPets] = useState([]);
  const [form, setForm] = useState({
    type: 'lost',      // 'lost' | 'found'
    pet_id: '',
    pet_name: '',
    comment: '',
    happened_at: '',
    latitude: null,
    longitude: null,
    photo: null,
  });

  // Carga inicial de reportes y mascotas propias
  const fetchReports = useCallback(async () => {
    try {
      const [rRes, pRes] = await Promise.all([
        api.get('/api/lost-reports'),
        api.get('/api/pets'),
      ]);

      // Asegurarnos de que data es array
      setReports(Array.isArray(rRes.data) ? rRes.data : []);
      setMyPets(Array.isArray(pRes.data) ? pRes.data : []);
    } catch (err) {
      console.error(err);
      addToast('Error cargando reportes o mascotas', 'error');
    }
  }, [addToast]);

  useEffect(() => {
    fetchReports();
  }, [fetchReports]);

  // Manejo de cambios en el formulario
  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setForm(f => ({
      ...f,
      [name]: name === 'photo' ? files[0] : value,
    }));
  };

  // Click sobre el mapa
  const handleMap = (lat, lng) =>
    setForm(f => ({ ...f, latitude: lat, longitude: lng }));

  // Envío del formulario
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const fd = new FormData();
      // Solo añadimos campos no nulos
      Object.entries(form).forEach(([k,v]) => {
        if (v != null && v !== '') fd.append(k, v);
      });
      await api.post('/api/lost-reports', fd);
      addToast('Reporte enviado', 'success');
      setForm({
        type: 'lost',
        pet_id: '',
        pet_name: '',
        comment: '',
        happened_at: '',
        latitude: null,
        longitude: null,
        photo: null,
      });
      fetchReports();
    } catch (err) {
      console.error(err.response?.data || err);
      addToast('Error al enviar el reporte', 'error');
    }
  };

  return (
    <div>
      <h1>Reportar Mascota Perdida o Encontrada</h1>

      {/* ─── Formulario ─────────────────────────────────────────────── */}
      <form onSubmit={handleSubmit} className="card p-4 mb-4">
        <select
          name="type"
          className="form-select mb-3"
          value={form.type}
          onChange={handleChange}
        >
          <option value="lost">Mi Mascota Perdida</option>
          <option value="found">Mascota Encontrada</option>
        </select>

        {form.type === 'lost' ? (
          <select
            name="pet_id"
            className="form-select mb-3"
            value={form.pet_id}
            onChange={handleChange}
            required
          >
            <option value="">Selecciona tu mascota</option>
            {myPets.map(p => (
              <option key={p.id} value={p.id}>{p.name}</option>
            ))}
          </select>
        ) : (
          <input
            name="pet_name"
            className="form-control mb-3"
            placeholder="Nombre de la mascota encontrada"
            value={form.pet_name}
            onChange={handleChange}
            required
          />
        )}

        <textarea
          name="comment"
          className="form-control mb-3"
          placeholder="Descripción"
          value={form.comment}
          onChange={handleChange}
          required
        />

        <input
          type="datetime-local"
          name="happened_at"
          className="form-control mb-3"
          value={form.happened_at}
          onChange={handleChange}
          required
        />

        <div className="mb-3">
          <label className="form-label">Ubicación (pincha en el mapa)</label>
          <MapPicker
            latitude={form.latitude}
            longitude={form.longitude}
            onChange={handleMap}
            readOnly={false}
          />
        </div>

        <input
          type="file"
          name="photo"
          className="form-control mb-3"
          accept="image/*"
          onChange={handleChange}
        />

        <button className="btn btn-danger">Reportar</button>
      </form>

      {/* ─── Listado de reportes ──────────────────────────────────────── */}
      <h2>Reportes</h2>
      <div className="row">
        {!Array.isArray(reports) || reports.length === 0 ? (
          <div className="col-12 text-center text-muted my-5">
            No hay reportes aún.
          </div>
        ) : (
          reports.map(report => (
            <div key={report.id} className="col-md-6 mb-3">
              <div className="card">
                {report.photo && (
                  <img
                    src={report.photo}
                    alt="foto reporte"
                    className="card-img-top"
                    style={{ height: 200, objectFit: 'cover' }}
                  />
                )}
                <div className="card-body">
                  <h5 className="card-title">
                    {report.type === 'lost'
                      ? report.pet?.name || 'Mascota perdida'
                      : report.pet_name || 'Mascota encontrada'}
                  </h5>
                  <p>{report.comment}</p>
                  <p>
                    <strong>Fecha:</strong>{' '}
                    {new Date(report.happened_at).toLocaleString()}
                  </p>
                  <p>
                    <strong>Ubicación:</strong> [{report.latitude},{' '}
                    {report.longitude}]
                  </p>
                  <p>
                    <strong>Reportado por:</strong>{' '}
                    {report.user?.name || 'Desconocido'}
                  </p>
                  {/* Sólo el creador puede marcarlo resuelto */}
                  {report.user?.id === user?.id && (
                    <button
                      className="btn btn-success btn-sm"
                      onClick={async () => {
                        try {
                          await api.post(
                            `/api/lost-reports/${report.id}/toggle-resolved`
                          );
                          addToast('Reporte actualizado', 'success');
                          fetchReports();
                        } catch {
                          addToast('Error al actualizar', 'error');
                        }
                      }}
                    >
                      {report.resolved ? 'Reabrir' : 'Marcar resuelto'}
                    </button>
                  )}
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
