import React, { useState, useEffect, useCallback } from 'react';
import {
  Routes,
  Route,
  Navigate,
  NavLink,
  useNavigate,
  useLocation
} from 'react-router-dom';
import api from './api/axios';
import Toast from './components/Toast';

// <-- Aquí vienen las rutas CORRECTAS a tus páginas:
import Pets from './pages/Pets';
import Activities from './pages/Activities';
import LostPets from './pages/LostPets';
import LostReports from './pages/LostReports';
import Register from './pages/Register';
import Login from './pages/Login';
import Profile from './pages/Profile';

export default function App() {
  const [toasts, setToasts] = useState([]);
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [user, setUser] = useState(null);
  const navigate = useNavigate();
  const location = useLocation();

  // Cuando cambia token, guardamos y cargamos perfil
  useEffect(() => {
    if (token) {
      localStorage.setItem('token', token);
      (async () => {
        try {
          const { data } = await api.get('/api/profile');
          setUser(data.user || data);
        } catch (e) {
          console.error(e);
        }
      })();
    } else {
      localStorage.removeItem('token');
      setUser(null);
    }
  }, [token]);

  // Toast helpers
  const addToast = useCallback((message, type = 'info') => {
    const id = Date.now() + Math.random();
    const bg =
      type === 'error'   ? '#dc3545'
    : type === 'success' ? '#28a745'
    : '#17a2b8';
    setToasts((ts) => [...ts, { id, message, bg }]);
  }, []);

  const removeToast = useCallback((id) => {
    setToasts((ts) => ts.filter((t) => t.id !== id));
  }, []);

  // Logout
  const handleLogout = async () => {
    try { await api.post('/api/logout'); } catch {}
    setToken(null);
    addToast('Has cerrado sesión', 'info');
    navigate('/login', { replace: true });
  };

  // Guardar rutas seguras vs públicas
  const RequireAuth = ({ children }) =>
    token ? children : <Navigate to="/login" replace state={{ from: location }} />;
  const RequireGuest = ({ children }) =>
    token ? <Navigate to="/pets" replace /> : children;

  return (
    <>
      {/* Navbar solo si estás logueado */}
      {token && user && (
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
          {/* … tu HTML de navbar como antes … */}
        </nav>
      )}

      {/* Toast container */}
      <div
        className="toast-container"
        style={{ position: 'fixed', top: '1rem', right: '1rem', zIndex: 1080 }}
      >
        {toasts.map((t) => (
          <Toast key={t.id} {...t} onClose={removeToast} />
        ))}
      </div>

      {/* Rutas */}
      <div className="container mt-4">
        <Routes>
          <Route path="/register" element={
            <RequireGuest><Register addToast={addToast} /></RequireGuest>
          }/>
          <Route path="/login" element={
            <RequireGuest><Login addToast={addToast} onLogin={setToken} /></RequireGuest>
          }/>
          <Route path="/profile" element={
            <RequireAuth><Profile addToast={addToast} user={user} /></RequireAuth>
          }/>
          <Route path="/pets" element={
            <RequireAuth><Pets addToast={addToast} user={user} /></RequireAuth>
          }/>
          <Route path="/activities" element={
            <RequireAuth><Activities addToast={addToast} user={user} /></RequireAuth>
          }/>
          <Route path="/lost-pets" element={
            <RequireAuth><LostPets addToast={addToast} user={user} /></RequireAuth>
          }/>
          <Route path="/lost-reports" element={
            <RequireAuth><LostReports addToast={addToast} user={user} /></RequireAuth>
          }/>
          <Route path="/" element={
            token 
              ? <Navigate to="/pets" replace /> 
              : <Navigate to="/login" replace />
          }/>
          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
      </div>
    </>
  );
}
