// PETCONNECT-FRONTEND/src/pages/LostPets.jsx
import React, { useState, useEffect } from 'react';
import api from '../api/axios';
import LostPetForm from '../components/LostPetForm';
import LostPetCard from '../components/LostPetCard';

export default function LostPets() {
  const [lostList, setLostList] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // 1) Carga inicial de mascotas perdidas
  useEffect(() => {
    const fetchLost = async () => {
      try {
        const res = await api.get('/api/lost-pets');
        const normalized = res.data.map(pet => ({
          ...pet,
          sightings: Array.isArray(pet.sightings) ? pet.sightings : []
        }));
        setLostList(normalized);
      } catch (err) {
        console.error(err);
        setError('No se pudieron cargar las mascotas perdidas.');
      } finally {
        setLoading(false);
      }
    };
    fetchLost();
  }, []);

  // 2) Reportar nueva mascota perdida
  const handleReport = async data => {
    try {
      const fd = new FormData();
      fd.append('pet_name', data.pet_name);
      fd.append('description', data.description);
      fd.append('last_seen_location', data.last_seen_location);
      if (data.photo) {
        fd.append('photo', data.photo);
      }

      const res = await api.post('/api/lost-pets', fd, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });

      setLostList([{ ...res.data, sightings: [] }, ...lostList]);
    } catch (err) {
      console.error('Error al reportar mascota perdida:', err.response?.data);
      if (err.response?.status === 422 && err.response.data.errors) {
        const messages = Object.values(err.response.data.errors)
          .flat()
          .join('\n');
        alert('Errores de validación:\n' + messages);
      } else {
        alert(
          'Error al reportar la mascota perdida:\n' +
            (err.response?.data?.message || err.message)
        );
      }
    }
  };

  // 3) Reportar avistamiento
  const handleSighting = async (id, sightingData) => {
    try {
      const res = await api.post(`/api/lost-pets/${id}/sightings`, sightingData);
      setLostList(cur =>
        cur.map(p =>
          p.id === id
            ? { ...p, sightings: [...(p.sightings || []), res.data] }
            : p
        )
      );
    } catch (err) {
      console.error(err);
      alert('Error al reportar avistamiento.');
    }
  };

  if (loading) return <p>Cargando mascotas perdidas...</p>;

  return (
    <div>
      <h2 className="mb-4">Mascotas Perdidas</h2>
      {error && <div className="alert alert-danger">{error}</div>}

      <LostPetForm onReport={handleReport} />

      <div className="row">
        {lostList.map(pet => (
          <div className="col-md-4 mb-3" key={pet.id}>
            <LostPetCard pet={pet} onReportSighting={handleSighting} />
          </div>
        ))}
      </div>
    </div>
  );
}
