import React, { useState, useEffect } from 'react';
import ReactStars from 'react-rating-stars-component';
import api from './api'; // Asegúrate de que esta ruta es correcta

const StarRater = ({ activityId }) => {
  const [average, setAverage] = useState(0);

  useEffect(() => {
    fetchAverage();
  }, []);

  const fetchAverage = async () => {
    try {
      const response = await api.get(`/api/activity-ratings/${activityId}/average`);
      setAverage(response.data.average);
    } catch (error) {
      console.error('Error al obtener la valoración promedio:', error);
    }
  };

  const ratingChanged = async (newRating) => {
    try {
      await api.post('/api/activity-ratings', {
        activity_id: activityId,
        rating: newRating,
      });
      fetchAverage();
    } catch (error) {
      console.error('Error al guardar la valoración:', error);
    }
  };

  return (
    <div>
      <ReactStars
        count={5}
        onChange={ratingChanged}
        size={24}
        activeColor="#ffd700"
      />
      <p>Valoración promedio: {average ? average.toFixed(1) : 'Sin valoraciones'}</p>
    </div>
  );
};

export default StarRater;
