import { useEffect, useState } from 'react';
import api from '../../api/axios';

export default function CommentAdmin() {
  const [comments, setComments] = useState([]);

  useEffect(() => {
    loadComments();
  }, []);

  const loadComments = async () => {
    try {
      const { data } = await api.get('/api/admin/comments');
      // Asegúrate de que data sea un array
      if (Array.isArray(data)) {
        setComments(data);
      } else if (Array.isArray(data.data)) {
        setComments(data.data); // por si viene como { data: [...] }
      } else {
        console.error('Respuesta inesperada:', data);
        setComments([]); // fallback seguro
      }
    } catch (err) {
      console.error('Error cargando comentarios:', err);
      setComments([]); // prevenir errores en render
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('¿Eliminar comentario?')) return;
    try {
      await api.delete(`/api/admin/comments/${id}`);
      setComments(comments.filter(c => c.id !== id));
    } catch (err) {
      console.error('Error al eliminar comentario:', err);
    }
  };

  return (
    <div>
      <h2>Gestión de Comentarios</h2>
      <ul className="list-group">
        {Array.isArray(comments) && comments.map(c => (
          <li key={c.id} className="list-group-item d-flex justify-content-between align-items-center">
            {c.body}
            <button className="btn btn-sm btn-danger" onClick={() => handleDelete(c.id)}>Eliminar</button>
          </li>
        ))}
        {(!Array.isArray(comments) || comments.length === 0) && (
          <li className="list-group-item text-muted">No hay comentarios.</li>
        )}
      </ul>
    </div>
  );
}
