import React, { useEffect, useState, Fragment } from 'react';
import api from '../api/axios';

export default function LostReports({ addToast }) {
  const [reports, setReports] = useState([]);
  const [loading, setLoading]   = useState(true);

  /* -------- fetch una sola vez -------- */
  useEffect(() => {
    (async () => {
      try {
        const res = await api.get('/api/lost-reports');
        // Asegura array: data.data (paginado) || data || []
        const arr =
          res.data?.data && Array.isArray(res.data.data)
            ? res.data.data
            : Array.isArray(res.data)
            ? res.data
            : [];
        setReports(arr);
      } catch {
        addToast('Error cargando reportes', 'error');
      } finally {
        setLoading(false);
      }
    })();
  }, []);           // ← array vacío: se ejecuta solo al montar

  /* -------- render -------- */
  return (
    <Fragment>
      <h1 className="mb-4">Reportes de Pérdidas</h1>

      {loading && <p>Cargando…</p>}

      {!loading && reports.length === 0 && (
        <p className="text-muted">No hay reportes.</p>
      )}

      {!loading && reports.length > 0 && (
        <ul className="list-group">
          {reports.map(r => (
            <li key={r.id} className="list-group-item">
              <strong>{r.pet_name}</strong> – {r.location ?? '—'} –
              {new Date(r.reported_at ?? r.posted_at).toLocaleDateString()}
            </li>
          ))}
        </ul>
      )}
    </Fragment>
  );
}
