// src/components/CustomToast.jsx
import React from 'react';

const CustomToast = ({ type, message }) => {
  const getIcon = () => {
    switch (type) {
      case 'success':
        return '✅';
      case 'error':
        return '❌';
      case 'info':
        return 'ℹ️';
      case 'warning':
        return '⚠️';
      default:
        return '';
    }
  };

  return (
    <div style={{ display: 'flex', alignItems: 'center' }}>
      <span style={{ fontSize: '1.5rem', marginRight: '0.5rem' }}>{getIcon()}</span>
      <span>{message}</span>
    </div>
  );
};

export default CustomToast;
