// src/pages/LostReports.jsx
import React, { useState, useEffect, useCallback } from 'react';
import api from '../api/axios';
import MapPicker from '../components/MapPicker';

export default function LostReports({ addToast, user }) {
  const [reports, setReports] = useState([]);
  const [form, setForm] = useState({
    type: 'lost',
    pet_id: '',
    pet_name: '',
    comment: '',
    happened_at: '',
    latitude: null,
    longitude: null,
    photo: null,
  });

  const fetchReports = useCallback(async () => {
    try {
      const res = await api.get('/api/lost-reports');
      setReports(res.data);
    } catch {
      addToast('Error cargando reportes', 'error');
    }
  }, [addToast]);

  useEffect(() => {
    fetchReports();
  }, [fetchReports]);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setForm((f) => ({
      ...f,
      [name]: name === 'photo' ? files[0] : value,
    }));
  };

  const handleMap = (lat, lng) => {
    setForm((f) => ({ ...f, latitude: lat, longitude: lng }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const fd = new FormData();
      Object.keys(form).forEach((key) => {
        if (form[key]) fd.append(key, form[key]);
      });

      await api.post('/api/lost-reports', fd);
      fetchReports();
      setForm({
        type: 'lost',
        pet_id: '',
        pet_name: '',
        comment: '',
        happened_at: '',
        latitude: null,
        longitude: null,
        photo: null,
      });
      addToast('Reporte enviado', 'success');
    } catch {
      addToast('Error al enviar el reporte', 'error');
    }
  };

  return (
    <div>
      <h1>Reportar Mascota Perdida o Encontrada</h1>
      <form onSubmit={handleSubmit} className="card p-4 mb-4">
        <select name="type" value={form.type} onChange={handleChange} className="form-select mb-3">
          <option value="lost">Reportar Mascota Perdida</option>
          <option value="found">Reportar Mascota Encontrada</option>
        </select>

        {form.type === 'lost' ? (
          <input
            name="pet_name"
            className="form-control mb-3"
            placeholder="Nombre de tu mascota"
            value={form.pet_name}
            onChange={handleChange}
          />
        ) : (
          <input
            name="pet_name"
            className="form-control mb-3"
            placeholder="Nombre de la mascota encontrada"
            value={form.pet_name}
            onChange={handleChange}
          />
        )}

        <textarea
          name="comment"
          className="form-control mb-3"
          placeholder="DescripciÃ³n del reporte"
          value={form.comment}
          onChange={handleChange}
        />

        <input
          type="datetime-local"
          name="happened_at"
          className="form-control mb-3"
          value={form.happened_at}
          onChange={handleChange}
        />

        <MapPicker latitude={form.latitude} longitude={form.longitude} onChange={handleMap} />

        <input type="file" name="photo" className="form-control mt-3" onChange={handleChange} />

        <button className="btn btn-primary mt-3">Enviar Reporte</button>
      </form>
    </div>
  );
}
