import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, useMapEvents } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Configurar íconos por defecto de Leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.3/images/marker-icon-2x.png',
  iconUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.3/images/marker-icon.png',
  shadowUrl:
    'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.9.3/images/marker-shadow.png',
});

// Componente para manejar eventos de mapa
function LocationMarker({ latitude, longitude, onChange, readOnly }) {
  const [position, setPosition] = useState([latitude, longitude]);

  useEffect(() => {
    if (latitude && longitude) {
      setPosition([latitude, longitude]);
    }
  }, [latitude, longitude]);

  useMapEvents({
    click(e) {
      if (!readOnly) {
        const { lat, lng } = e.latlng;
        setPosition([lat, lng]);
        if (onChange) onChange(lat, lng);
      }
    },
  });

  return position ? <Marker position={position} /> : null;
}

// Componente principal MapPicker
export default function MapPicker({ latitude = 40.4168, longitude = -3.7038, onChange, readOnly = false }) {
  return (
    <MapContainer
      center={[latitude, longitude]}
      zoom={13}
      style={{ height: 300, width: '100%' }}
    >
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
      <LocationMarker latitude={latitude} longitude={longitude} onChange={onChange} readOnly={readOnly} />
    </MapContainer>
  );
}
