// src/pages/LostReports.jsx
import React, { useState, useEffect, useCallback } from 'react';
import api from '../api/axios';
import MapPicker from '../components/MapPicker';

export default function LostReports({ addToast, user }) {
  const [reports, setReports] = useState([]);
  const [myPets, setMyPets] = useState([]);
  const [form, setForm] = useState({
    type: 'lost',
    pet_id: '',
    pet_name: '',
    comment: '',
    happened_at: '',
    latitude: 40.4168, // Valor inicial (Madrid)
    longitude: -3.7038, // Valor inicial (Madrid)
    photo: null,
  });

  // Cargar reportes y mascotas del usuario
  const fetchReports = useCallback(async () => {
    try {
      const [reportsRes, petsRes] = await Promise.all([
        api.get('/api/lost-reports'),
        api.get('/api/pets')
      ]);
      setReports(reportsRes.data);
      setMyPets(petsRes.data);
    } catch {
      addToast('Error cargando reportes o mascotas', 'error');
    }
  }, [addToast]);

  useEffect(() => {
    fetchReports();
  }, [fetchReports]);

  // Manejar cambios en el formulario
  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setForm((f) => ({
      ...f,
      [name]: name === 'photo' ? files[0] : value,
    }));
  };

  const handleMap = (lat, lng) => {
    setForm((f) => ({ ...f, latitude: lat, longitude: lng }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const fd = new FormData();
      Object.keys(form).forEach((key) => {
        if (form[key]) fd.append(key, form[key]);
      });

      await api.post('/api/lost-reports', fd);
      fetchReports();
      addToast('Reporte enviado', 'success');
      setForm({
        type: 'lost',
        pet_id: '',
        pet_name: '',
        comment: '',
        happened_at: '',
        latitude: 40.4168,
        longitude: -3.7038,
        photo: null,
      });
    } catch (err) {
      addToast('Error al enviar el reporte', 'error');
      console.error(err.response?.data || err);
    }
  };

  return (
    <div>
      <h1>Reportar Mascota Perdida o Encontrada</h1>

      <form onSubmit={handleSubmit} className="card p-4 mb-4">
        <select name="type" value={form.type} onChange={handleChange} className="form-select mb-3">
          <option value="lost">Reportar Mascota Perdida</option>
          <option value="found">Reportar Mascota Encontrada</option>
        </select>

        {form.type === 'lost' ? (
          <select
            name="pet_id"
            value={form.pet_id}
            onChange={handleChange}
            className="form-select mb-3"
            required
          >
            <option value="">Selecciona tu mascota</option>
            {myPets.map((pet) => (
              <option key={pet.id} value={pet.id}>
                {pet.name}
              </option>
            ))}
          </select>
        ) : (
          <input
            name="pet_name"
            className="form-control mb-3"
            placeholder="Nombre de la mascota encontrada"
            value={form.pet_name}
            onChange={handleChange}
            required
          />
        )}

        <textarea
          name="comment"
          className="form-control mb-3"
          placeholder="Descripción del reporte"
          value={form.comment}
          onChange={handleChange}
          required
        />

        <input
          type="datetime-local"
          name="happened_at"
          className="form-control mb-3"
          value={form.happened_at}
          onChange={handleChange}
          required
        />

        <MapPicker
          latitude={form.latitude}
          longitude={form.longitude}
          onChange={handleMap}
        />

        <div className="mt-3 mb-3">
          <input type="file" name="photo" className="form-control" onChange={handleChange} />
        </div>

        <button className="btn btn-danger">Reportar</button>
      </form>

      <h2>Reportes</h2>
      <div className="row">
        {reports.map((report) => (
          <div key={report.id} className="col-md-6 mb-3">
            <div className="card">
              {report.photo && <img src={report.photo} alt="Reporte" className="card-img-top" />}
              <div className="card-body">
                <h5 className="card-title">{report.pet ? report.pet.name : report.pet_name}</h5>
                <p>{report.comment}</p>
                <p><strong>Ubicación:</strong> [{report.latitude}, {report.longitude}]</p>
                {report.latitude && report.longitude && (
                  <MapContainer
                    center={[report.latitude, report.longitude]}
                    zoom={15}
                    style={{ height: 200 }}
                  >
                    <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
                    <Marker position={[report.latitude, report.longitude]} />
                  </MapContainer>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
