// src/pages/LostReports.jsx
import React, { useState, useEffect, useCallback } from 'react';
import api from '../api/axios';
import MapPicker from '../components/MapPicker';

export default function LostReports({ addToast, user }) {
  const [reports, setReports] = useState([]);
  const [myPets, setMyPets] = useState([]);
  const [form, setForm] = useState({
    type: 'lost', // 'lost' | 'found'
    pet_id: '',
    pet_name: '',
    comment: '',
    happened_at: '',
    latitude: null,
    longitude: null,
    photo: null,
  });
  const [commentForm, setCommentForm] = useState('');
  const [comments, setComments] = useState({});

  // Obtener los reportes propios y las mascotas del usuario
  const fetchReports = useCallback(async () => {
    try {
      const [reportsRes, petsRes] = await Promise.all([
        api.get('/api/lost-reports'),
        api.get('/api/pets')
      ]);
      // Filtrar solo los reportes del usuario autenticado
      setReports(reportsRes.data.filter(report => report.user_id === user.id));
      setMyPets(petsRes.data);
    } catch {
      addToast('Error cargando reportes o mascotas', 'error');
    }
  }, [addToast, user]);

  useEffect(() => {
    fetchReports();
  }, [fetchReports]);

  // Manejar cambios en el formulario
  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setForm((f) => ({
      ...f,
      [name]: name === 'photo' ? files[0] : value,
    }));
  };

  const handleMap = (lat, lng) => {
    setForm((f) => ({ ...f, latitude: lat, longitude: lng }));
  };

  // Enviar reporte
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const fd = new FormData();
      Object.keys(form).forEach((key) => {
        if (form[key]) fd.append(key, form[key]);
      });

      await api.post('/api/lost-reports', fd);
      fetchReports();
      addToast('Reporte enviado', 'success');
      setForm({
        type: 'lost',
        pet_id: '',
        pet_name: '',
        comment: '',
        happened_at: '',
        latitude: null,
        longitude: null,
        photo: null,
      });
    } catch {
      addToast('Error al enviar el reporte', 'error');
    }
  };

  // Manejar comentarios
  const handleCommentChange = (e) => {
    setCommentForm(e.target.value);
  };

  const handleCommentSubmit = async (reportId) => {
    try {
      await api.post(`/api/lost-reports/${reportId}/comments`, { body: commentForm });
      addToast('Comentario enviado', 'success');
      setCommentForm('');
      fetchReports();
    } catch {
      addToast('Error al enviar el comentario', 'error');
    }
  };

  return (
    <div>
      <h1>Mis Reportes de Mascotas Perdidas o Encontradas</h1>

      {/* Formulario de reporte */}
      <form onSubmit={handleSubmit} className="card p-4 mb-4">
        <select
          name="type"
          value={form.type}
          onChange={handleChange}
          className="form-select mb-3"
        >
          <option value="lost">Reportar Mascota Perdida</option>
          <option value="found">Reportar Mascota Encontrada</option>
        </select>

        {form.type === 'lost' ? (
          <select
            name="pet_id"
            value={form.pet_id}
            onChange={handleChange}
            className="form-select mb-3"
            required
          >
            <option value="">Selecciona tu mascota</option>
            {myPets.map((pet) => (
              <option key={pet.id} value={pet.id}>
                {pet.name}
              </option>
            ))}
          </select>
        ) : (
          <input
            name="pet_name"
            className="form-control mb-3"
            placeholder="Nombre de la mascota encontrada"
            value={form.pet_name}
            onChange={handleChange}
            required
          />
        )}

        <textarea
          name="comment"
          className="form-control mb-3"
          placeholder="Descripción del reporte"
          value={form.comment}
          onChange={handleChange}
          required
        />

        <input
          type="datetime-local"
          name="happened_at"
          className="form-control mb-3"
          value={form.happened_at}
          onChange={handleChange}
          required
        />

        <MapPicker
          latitude={form.latitude}
          longitude={form.longitude}
          onChange={handleMap}
        />

        <div className="mt-3 mb-3">
          <input
            type="file"
            name="photo"
            className="form-control"
            onChange={handleChange}
          />
        </div>

        <button className="btn btn-danger">Reportar</button>
      </form>

      {/* Listado de reportes */}
      <h2>Mis Reportes</h2>
      <div className="row">
        {reports.map((report) => (
          <div key={report.id} className="col-md-6 mb-3">
            <div className="card">
              {report.photo && (
                <img
                  src={report.photo}
                  alt="Reporte"
                  className="card-img-top"
                  style={{ height: 200, objectFit: 'cover' }}
                />
              )}
              <div className="card-body">
                <h5 className="card-title">
                  {report.type === 'lost' ? 'Mascota Perdida' : 'Mascota Encontrada'}
                </h5>
                <p>{report.comment}</p>
                <p>
                  <strong>Fecha:</strong> {new Date(report.happened_at).toLocaleString()}
                </p>
                <p>
                  <strong>Ubicación:</strong> [{report.latitude}, {report.longitude}]
                </p>

                {/* Comentarios */}
                <div className="mt-3">
                  <h6>Comentarios</h6>
                  {report.comments?.map((c) => (
                    <p key={c.id}><strong>{c.user?.name}:</strong> {c.body}</p>
                  ))}
                  <input
                    type="text"
                    className="form-control mb-2"
                    placeholder="Escribe un comentario..."
                    value={commentForm}
                    onChange={handleCommentChange}
                  />
                  <button
                    className="btn btn-primary btn-sm"
                    onClick={() => handleCommentSubmit(report.id)}
                  >
                    Comentar
                  </button>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
