// PETCONNECT-FRONTEND/src/components/LostPetForm.jsx
import React, { useState } from 'react';
import api from '../api/axios';    // <<< Muy importante

export default function LostPetForm({ onReport }) {
  const [form, setForm] = useState({
    pet_name: '',
    description: '',
    last_seen_location: '',
    photo: null,
  });
  const [loading, setLoading] = useState(false);

  const handleChange = e => {
    const { name, value, files } = e.target;
    if (name === 'photo') {
      console.log('Archivo seleccionado:', files[0]);
      setForm(f => ({ ...f, photo: files[0] }));
    } else {
      setForm(f => ({ ...f, [name]: value }));
    }
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setLoading(true);
    try {
      const fd = new FormData();
      fd.append('pet_name', form.pet_name);
      fd.append('description', form.description);
      fd.append('last_seen_location', form.last_seen_location);
      if (form.photo) fd.append('photo', form.photo);

      console.log('Enviando FormData:');
      for (let pair of fd.entries()) {
        console.log(pair[0], pair[1]);
      }

      const res = await api.post('/api/lost-pets', fd, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      onReport(res.data);
      setForm({
        pet_name: '',
        description: '',
        last_seen_location: '',
        photo: null,
      });
    } catch (err) {
      // Muestra TODO el objeto de error
      console.error('ERROR completo en handleSubmit:', err);

      // Si hay response y errores de validación, los mostramos
      if (err.response?.status === 422 && err.response.data.errors) {
        const messages = Object.values(err.response.data.errors)
          .flat()
          .join('\n');
        alert('Errores de validación:\n' + messages);
      } else if (err.response) {
        alert('Error del servidor:\n' + JSON.stringify(err.response.data));
      } else {
        alert('Error inesperado:\n' + err.message);
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="card mb-4 shadow-sm">
      <div className="card-body">
        <h5 className="card-title">Reportar mascota perdida</h5>
        <form onSubmit={handleSubmit}>
          <input
            name="pet_name"
            className="form-control mb-2"
            placeholder="Nombre de la mascota"
            value={form.pet_name}
            onChange={handleChange}
            required
          />
          <input
            name="description"
            className="form-control mb-2"
            placeholder="Descripción"
            value={form.description}
            onChange={handleChange}
          />
          <input
            name="last_seen_location"
            className="form-control mb-2"
            placeholder="Última ubicación"
            value={form.last_seen_location}
            onChange={handleChange}
          />
          <input
            name="photo"
            type="file"
            accept="image/*"
            className="form-control mb-3"
            onChange={handleChange}
          />
          <button type="submit" className="btn btn-danger" disabled={loading}>
            {loading ? 'Publicando...' : 'Publicar'}
          </button>
        </form>
      </div>
    </div>
  );
}
