import React, { useState, useEffect } from 'react';
import api from '../api/axios';
import MapPicker from '../components/MapPicker';

export default function LostReports({ addToast, user }) {
  const [mode, setMode]       = useState('lost');
  const [reports, setReports] = useState([]);
  const [form, setForm]       = useState({
    pet_id: '', comment: '', happened_at: '',
    latitude: null, longitude: null, photo: null
  });

  // 1) Cargar reportes
  useEffect(() => {
    api.get('/lost-reports', { params:{ type: mode } })
       .then(res => setReports(res.data))
       .catch(() => addToast('Error cargando reportes','error'));
  }, [mode]);

  // 2) Inputs
  const handleChange = e => {
    const { name, value, files } = e.target;
    setForm(f => ({ 
      ...f, 
      [name]: name==='photo' ? files[0] : value 
    }));
  };
  const handleMap = (lat,lng) => {
    setForm(f => ({ ...f, latitude: lat, longitude: lng }));
  };

  // 3) Envío
  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const fd = new FormData();
      Object.entries(form).forEach(([k,v])=>
        v!=null && v!=='' && fd.append(k,v)
      );
      fd.append('type', mode);
      await api.post('/lost-reports', fd, {
        headers:{ 'Content-Type':'multipart/form-data' }
      });
      addToast('Reporte enviado','success');
      const r2 = await api.get('/lost-reports',{ params:{ type: mode }});
      setReports(r2.data);
      setForm({ pet_id:'', comment:'', happened_at:'', latitude:null, longitude:null, photo:null });
    } catch {
      addToast('Error al enviar','error');
    }
  };

  return (
    <div className="container mt-4">
      <div className="btn-group mb-3">
        <button
          onClick={()=>setMode('lost')}
          className={`btn ${mode==='lost'?'btn-danger':'btn-outline-danger'}`}
        >¿Has perdido tu mascota?</button>
        <button
          onClick={()=>setMode('found')}
          className={`btn ${mode==='found'?'btn-success':'btn-outline-success'}`}
        >¿Has encontrado una mascota?</button>
      </div>

      <form onSubmit={handleSubmit} className="card p-3 mb-4">
        {mode==='lost' && (
          <select
            name="pet_id"
            className="form-select mb-2"
            value={form.pet_id}
            onChange={handleChange}
            required
          >
            <option value="">Selecciona tu mascota</option>
            {user.pets.map(p=>(
              <option key={p.id} value={p.id}>{p.pet_name}</option>
            ))}
          </select>
        )}

        <textarea
          name="comment"
          className="form-control mb-2"
          placeholder="Comentario"
          value={form.comment}
          onChange={handleChange}
        />

        <input
          type="datetime-local"
          name="happened_at"
          className="form-control mb-2"
          value={form.happened_at}
          onChange={handleChange}
        />

        <label className="form-label mb-1">Ubicación (click en el mapa)</label>
        <MapPicker
          latitude={form.latitude}
          longitude={form.longitude}
          onChange={handleMap}
        />

        <div className="mb-3 mt-3">
          <input
            type="file"
            name="photo"
            accept="image/*"
            className="form-control"
            onChange={handleChange}
          />
        </div>

        <button className={`btn ${mode==='lost'?'btn-danger':'btn-success'}`}>
          Reportar {mode==='lost'?'Pérdida':'Hallazgo'}
        </button>
      </form>

      <h3>Listado de {mode==='lost'?'pérdidas':'hallazgos'} ({reports.length})</h3>
      <div className="row">
        {reports.map(r => (
          <div key={r.id} className="col-md-6 mb-3">
            <div className="card h-100 p-3">
              {r.type==='lost' && <h5>Perdida de: {r.pet?.pet_name}</h5>}
              {r.type==='found' && <h5>Hallazgo</h5>}
              <p>{r.comment}</p>
              <small className="text-muted">
                {r.user.name} · {new Date(r.happened_at).toLocaleString()}
              </small>
              {r.latitude!=null && (
                <p>
                  <strong>Ubicación:</strong> {r.latitude.toFixed(5)}, {r.longitude.toFixed(5)}
                </p>
              )}
              {r.photo && (
                <img
                  src={r.photo}
                  alt="foto"
                  className="img-fluid mt-2"
                  style={{ maxWidth: '100%' }}
                />
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
