// src/pages/LostPets.jsx
import React, { useState, useEffect, useMemo, useCallback, Fragment } from 'react';
import { Link } from 'react-router-dom';
import { MapContainer, TileLayer, Marker } from 'react-leaflet';
import L from 'leaflet';
import api from '../api/axios';

/* Leaflet default icon fix */
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

/* Component */
export default function LostPets({ addToast, user }) {
  const [lostPets, setLostPets] = useState([]);
  const [tab, setTab] = useState('lost'); // 'lost' | 'found'
  const [expandedId, setExpandedId] = useState(null);

  /* Fetch lost pets */
  const fetchLostPets = useCallback(async () => {
    try {
      const res = await api.get('/api/lost-pets');
      setLostPets(res.data);
    } catch {
      addToast('Error cargando mascotas perdidas', 'error');
    }
  }, [addToast]);

  useEffect(() => {
    fetchLostPets();
  }, [fetchLostPets]);

  /* Filtered list */
  const filtered = useMemo(() => {
    return lostPets.filter(p => (tab === 'lost' ? !p.found : p.found));
  }, [lostPets, tab]);

  /* Toggle details */
  const toggleDetails = id => setExpandedId(expandedId === id ? null : id);

  return (
    <Fragment>
      <h1 className="mb-4">Mascotas Perdidas</h1>

      {/* Tabs */}
      <div className="mb-3">
        <button
          className={`btn ${tab === 'lost' ? 'btn-primary' : 'btn-outline-primary'}`}
          onClick={() => setTab('lost')}
        >
          Mascotas Perdidas
        </button>
        <button
          className={`btn ms-2 ${tab === 'found' ? 'btn-primary' : 'btn-outline-primary'}`}
          onClick={() => setTab('found')}
        >
          Mascotas Encontradas
        </button>
      </div>

      {/* List of Lost Pets */}
      <div className="row">
        {filtered.length === 0 ? (
          <p className="text-center text-muted">No hay mascotas {tab === 'lost' ? 'perdidas' : 'encontradas'}.</p>
        ) : (
          filtered.map(p => (
            <div key={p.id} className="col-md-6 mb-4">
              <div className="card">
                {p.photo && (
                  <img src={p.photo} alt={p.pet_name} className="card-img-top" style={{ height: 200, objectFit: 'cover' }} />
                )}
                <div className="card-body">
                  <h5 className="card-title">{p.pet_name || 'Mascota Desconocida'}</h5>
                  <p>{p.description}</p>
                  {p.last_seen_location && <p><strong>Ubicación:</strong> {p.last_seen_location}</p>}
                  {p.last_seen_latitude && p.last_seen_longitude && (
                    <MapContainer
                      center={[p.last_seen_latitude, p.last_seen_longitude]}
                      zoom={14}
                      style={{ height: 200 }}
                    >
                      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
                      <Marker position={[p.last_seen_latitude, p.last_seen_longitude]} />
                    </MapContainer>
                  )}
                  <button
                    className="btn btn-sm btn-info mt-2"
                    onClick={() => toggleDetails(p.id)}
                  >
                    {expandedId === p.id ? 'Ocultar detalles' : 'Ver detalles'}
                  </button>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </Fragment>
  );
}
