// src/components/ActivityCard.jsx
import React, { useState, useEffect } from 'react';
import ActivityMap from './ActivityMap';
import StarRater from './StarRater';

export default function ActivityCard({
  activity,
  onJoin,
  onRate,
  addToast
}) {
  const {
    title,
    user,
    starts_at,
    ends_at,
    location,
    is_registered,
    is_finished,
    participants_count,
    average_rating,
    latitude,
    longitude,
    description
  } = activity;

  const [localCount, setLocalCount] = useState(participants_count || 0);
  const [localAverage, setLocalAverage] = useState(average_rating || 0);

  const handleRate = async (newRating) => {
    if (onRate) {
      await onRate(newRating);
      setLocalAverage(newRating);
    }
  };

  useEffect(() => {
    setLocalCount(participants_count || 0);
    setLocalAverage(average_rating || 0);
  }, [participants_count, average_rating]);

  return (
    <div className="card mb-3">
      <div className="card-body">
        <h5 className="card-title">{title}</h5>
        <p className="text-muted small mb-2">Propuesta por: {user?.name || '-'}</p>
        {description && <p className="card-text">{description}</p>}
        <p><strong>Inicio:</strong> {new Date(starts_at).toLocaleString()}</p>
        {ends_at && <p><strong>Fin:</strong> {new Date(ends_at).toLocaleString()}</p>}
        <p><strong>Asistentes:</strong> {localCount}</p>
        <StarRater
          activityId={activity.id}
          initialValue={localAverage}
          readOnly={!is_finished}
          onRate={handleRate}
        />
        <button 
          className="btn btn-primary mt-2"
          onClick={onJoin}
          disabled={is_registered}
        >
          {is_registered ? 'Ya inscrito' : 'Apuntarse'}
        </button>
      </div>
    </div>
  );
}
