import React, { useState, useEffect, useCallback } from 'react';
import api from '../api/axios';

export default function LostReportForm({ addToast, user }) {
  const [reportType, setReportType] = useState('lost'); // 'lost' | 'found'
  const [myPets, setMyPets] = useState([]);
  const [form, setForm] = useState({
    pet_id: '',
    pet_name: '',
    description: '',
    happened_at: '',
    latitude: '',
    longitude: '',
    photo: null,
  });

  // Cargar las mascotas del usuario para el select
  const fetchMyPets = useCallback(async () => {
    try {
      const res = await api.get('/api/pets');
      setMyPets(res.data);
    } catch (err) {
      addToast('Error cargando tus mascotas', 'error');
    }
  }, [addToast]);

  useEffect(() => {
    fetchMyPets();
  }, [fetchMyPets]);

  // Cambios en formulario
  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setForm((f) => ({
      ...f,
      [name]: name === 'photo' ? files[0] : value,
    }));
  };

  // Cambiar tipo de reporte limpia el formulario y selecciona tipo
  const handleReportTypeChange = (e) => {
    const newType = e.target.value;
    setReportType(newType);
    setForm({
      pet_id: '',
      pet_name: '',
      description: '',
      happened_at: '',
      latitude: '',
      longitude: '',
      photo: null,
    });
  };

  // Enviar formulario
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const fd = new FormData();
      fd.append('type', reportType);

      if (reportType === 'lost') {
        if (!form.pet_id) {
          addToast('Debes seleccionar una mascota', 'error');
          return;
        }
        fd.append('pet_id', form.pet_id);
      } else {
        if (!form.pet_name.trim()) {
          addToast('Debes escribir el nombre o descripción de la mascota encontrada', 'error');
          return;
        }
        fd.append('pet_name', form.pet_name);
      }

      if (!form.happened_at || !form.latitude || !form.longitude) {
        addToast('Fecha y ubicación son obligatorias', 'error');
        return;
      }

      fd.append('description', form.description || '');
      fd.append('happened_at', form.happened_at);
      fd.append('latitude', form.latitude);
      fd.append('longitude', form.longitude);

      if (form.photo) {
        fd.append('photo', form.photo);
      }

      await api.post('/api/lost-reports', fd);

      addToast('Reporte enviado con éxito', 'success');

      // Limpiar formulario después de enviar
      setForm({
        pet_id: '',
        pet_name: '',
        description: '',
        happened_at: '',
        latitude: '',
        longitude: '',
        photo: null,
      });
    } catch (err) {
      addToast('Error al enviar reporte', 'error');
      console.error(err);
    }
  };

  return (
    <div className="card p-4">
      <h2>Reportar Mascota Perdida o Encontrada</h2>

      <div className="mb-3">
        <label>Tipo de reporte</label>
        <select
          className="form-select"
          value={reportType}
          onChange={handleReportTypeChange}
        >
          <option value="lost">Mascota Perdida</option>
          <option value="found">Mascota Encontrada</option>
        </select>
      </div>

      <form onSubmit={handleSubmit}>

        {reportType === 'lost' ? (
          <div className="mb-3">
            <label>Selecciona tu mascota</label>
            <select
              className="form-select"
              name="pet_id"
              value={form.pet_id}
              onChange={handleChange}
              required
            >
              <option value="">-- Selecciona --</option>
              {myPets.map((pet) => (
                <option key={pet.id} value={pet.id}>
                  {pet.name}
                </option>
              ))}
            </select>
          </div>
        ) : (
          <div className="mb-3">
            <label>Nombre o descripción de la mascota encontrada</label>
            <input
              type="text"
              className="form-control"
              name="pet_name"
              value={form.pet_name}
              onChange={handleChange}
              placeholder="Ej: Perro marrón pequeño"
              required
            />
          </div>
        )}

        <div className="mb-3">
          <label>Descripción</label>
          <textarea
            className="form-control"
            name="description"
            value={form.description}
            onChange={handleChange}
            placeholder="Detalles adicionales (opcional)"
          />
        </div>

        <div className="mb-3">
          <label>Fecha y hora</label>
          <input
            type="datetime-local"
            className="form-control"
            name="happened_at"
            value={form.happened_at}
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-3">
          <label>Latitud</label>
          <input
            type="number"
            step="any"
            className="form-control"
            name="latitude"
            value={form.latitude}
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-3">
          <label>Longitud</label>
          <input
            type="number"
            step="any"
            className="form-control"
            name="longitude"
            value={form.longitude}
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-3">
          <label>Foto (opcional)</label>
          <input
            type="file"
            className="form-control"
            name="photo"
            onChange={handleChange}
            accept="image/*"
          />
        </div>

        <button type="submit" className="btn btn-primary">
          Enviar reporte
        </button>
      </form>
    </div>
  );
}
