// src/pages/LostReports.jsx
import React, { useState, useEffect, useCallback } from 'react';
import api from '../api/axios';
import MapPicker from '../components/MapPicker';

export default function LostReports({ addToast }) {
  const [reports, setReports] = useState([]);
  const [form, setForm] = useState({
    type: 'lost', // 'lost' | 'found'
    pet_name: '',
    comment: '',
    happened_at: '',
    latitude: null,
    longitude: null,
    photo: null,
  });

  /* Fetch reports */
  const fetchReports = useCallback(async () => {
    try {
      const { data } = await api.get('/api/lost-reports');
      setReports(data);
    } catch {
      addToast('Error cargando reportes', 'error');
    }
  }, [addToast]);

  useEffect(() => {
    fetchReports();
  }, [fetchReports]);

  /* Form handlers */
  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setForm((f) => ({
      ...f,
      [name]: name === 'photo' ? files[0] : value,
    }));
  };

  const handleMap = (lat, lng) => {
    setForm((f) => ({ ...f, latitude: lat, longitude: lng }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const fd = new FormData();
      Object.keys(form).forEach(key => {
        if (form[key]) fd.append(key, form[key]);
      });

      await api.post('/api/lost-reports', fd);
      fetchReports();
      addToast('Reporte enviado', 'success');
      setForm({ type: 'lost', pet_name: '', comment: '', happened_at: '', latitude: null, longitude: null, photo: null });
    } catch {
      addToast('Error al enviar el reporte', 'error');
    }
  };

  return (
    <div>
      <h1>Reportar Mascota Perdida o Encontrada</h1>
      <form onSubmit={handleSubmit} className="card p-4 mb-4">
        <select name="type" value={form.type} onChange={handleChange} className="form-select mb-3">
          <option value="lost">Reportar Mascota Perdida</option>
          <option value="found">Reportar Mascota Encontrada</option>
        </select>
        
        <textarea
          name="comment"
          className="form-control mb-3"
          placeholder="DescripciÃ³n"
          value={form.comment}
          onChange={handleChange}
          required
        />

        <input
          type="datetime-local"
          name="happened_at"
          className="form-control mb-3"
          value={form.happened_at}
          onChange={handleChange}
          required
        />

        <MapPicker latitude={form.latitude} longitude={form.longitude} onChange={handleMap} />

        <input type="file" name="photo" className="form-control mt-3" onChange={handleChange} />

        <button className="btn btn-danger mt-3">Reportar</button>
      </form>
    </div>
  );
}
