// src/pages/LostPets.jsx
import React, { useState, useEffect, useMemo, useCallback, Fragment } from 'react';
import { MapContainer, TileLayer, Marker } from 'react-leaflet';
import L from 'leaflet';
import api from '../api/axios';

/* Leaflet default icon fix */
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

/* Utility for calculating distance (Haversine) */
function distanceKm(lat1, lon1, lat2, lon2) {
  const R = 6371; // Earth radius in km
  const dLat = ((lat2 - lat1) * Math.PI) / 180;
  const dLon = ((lon2 - lon1) * Math.PI) / 180;
  const a =
    Math.sin(dLat / 2) ** 2 +
    Math.cos((lat1 * Math.PI) / 180) *
      Math.cos((lat2 * Math.PI) / 180) *
      Math.sin(dLon / 2) ** 2;
  return 2 * R * Math.asin(Math.sqrt(a));
}

export default function LostPets({ addToast, user }) {
  const [lostPets, setLostPets] = useState([]);
  const [expandedId, setExpandedId] = useState(null);

  // Filters
  const [filters, setFilters] = useState({
    name: '',
    dateOrder: 'desc', // 'asc' or 'desc'
    maxKm: 50, // Maximum distance in km for "near me"
    nearMe: false,
  });

  /* Fetch lost pets */
  const fetchLostPets = useCallback(async () => {
    try {
      const res = await api.get('/api/lost-reports');
      setLostPets(res.data);
    } catch {
      addToast('Error cargando mascotas perdidas', 'error');
    }
  }, [addToast]);

  useEffect(() => {
    fetchLostPets();
  }, [fetchLostPets]);

  /* Filtered list */
  const filtered = useMemo(() => {
    return lostPets
      .filter((p) => {
        // Filtrar por nombre
        if (filters.name && !p.pet_name?.toLowerCase().includes(filters.name.toLowerCase())) {
          return false;
        }

        // Filtrar por cercanía (si está activado)
        if (filters.nearMe && user?.latitude && user?.longitude) {
          const km = distanceKm(user.latitude, user.longitude, p.latitude, p.longitude);
          if (km > filters.maxKm) return false;
        }

        return true;
      })
      .sort((a, b) => {
        const dateA = new Date(a.happened_at);
        const dateB = new Date(b.happened_at);
        return filters.dateOrder === 'asc' ? dateA - dateB : dateB - dateA;
      });
  }, [lostPets, filters, user]);

  /* Toggle details */
  const toggleDetails = (id) => setExpandedId(expandedId === id ? null : id);

  /* Update filter */
  const updateFilter = (field, value) => {
    setFilters((prev) => ({ ...prev, [field]: value }));
  };

  return (
    <Fragment>
      <h1 className="mb-4">Mascotas Perdidas y Encontradas</h1>

      {/* Filter panel */}
      <div className="card mb-4 p-3">
        <div className="row g-2">
          <div className="col-md">
            <input
              type="text"
              className="form-control"
              placeholder="Buscar por nombre..."
              value={filters.name}
              onChange={(e) => updateFilter('name', e.target.value)}
            />
          </div>

          <div className="col-md">
            <select
              className="form-select"
              value={filters.dateOrder}
              onChange={(e) => updateFilter('dateOrder', e.target.value)}
            >
              <option value="desc">Más recientes primero</option>
              <option value="asc">Más antiguas primero</option>
            </select>
          </div>

          <div className="col-md">
            <div className="form-check">
              <input
                type="checkbox"
                className="form-check-input"
                checked={filters.nearMe}
                onChange={(e) => updateFilter('nearMe', e.target.checked)}
              />
              <label className="form-check-label">Cerca de mí</label>
            </div>
            {filters.nearMe && (
              <input
                type="number"
                className="form-control mt-2"
                value={filters.maxKm}
                onChange={(e) => updateFilter('maxKm', Number(e.target.value))}
                placeholder="Máxima distancia (km)"
              />
            )}
          </div>
        </div>
      </div>

      {/* List of Lost Pets */}
      <div className="row">
        {filtered.length === 0 ? (
          <p className="text-muted">No hay mascotas en esta categoría.</p>
        ) : (
          filtered.map((p) => (
            <div key={p.id} className="col-md-6 mb-4">
              <div className="card">
                {p.photo && (
                  <img
                    src={p.photo}
                    alt={p.pet_name || 'Mascota'}
                    className="card-img-top"
                    style={{ height: 200, objectFit: 'cover' }}
                  />
                )}
                <div className="card-body">
                  <h5 className="card-title">{p.pet_name || 'Mascota Desconocida'}</h5>
                  <p>{p.comment}</p>
                  <p>
                    <strong>Ubicación:</strong> [{p.latitude}, {p.longitude}]
                  </p>
                  <p>
                    <strong>Fecha:</strong> {new Date(p.happened_at).toLocaleString()}
                  </p>
                  <button
                    className="btn btn-sm btn-info mt-2"
                    onClick={() => toggleDetails(p.id)}
                  >
                    {expandedId === p.id ? 'Ocultar detalles' : 'Ver detalles'}
                  </button>

                  {expandedId === p.id && p.latitude && p.longitude && (
                    <MapContainer center={[p.latitude, p.longitude]} zoom={14} style={{ height: 200 }}>
                      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
                      <Marker position={[p.latitude, p.longitude]} />
                    </MapContainer>
                  )}
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </Fragment>
  );
}
