// src/pages/LostReports.jsx

import React, { useState, useEffect, useCallback } from 'react';
import api from '../api/axios';
import MapPicker from '../components/MapPicker';

export default function LostReports({ addToast }) {
  const [reports, setReports] = useState([]);
  const [form, setForm] = useState({
    type: 'lost', // 'lost' | 'found'
    pet_id: '',
    pet_name: '', // Necesario para mascotas encontradas
    comment: '',
    happened_at: '',
    latitude: null,
    longitude: null,
    photo: null,
  });

  /* Fetch reports */
  const fetchReports = useCallback(async () => {
    try {
      const { data } = await api.get('/api/lost-reports');
      setReports(data);
    } catch {
      addToast('Error cargando reportes', 'error');
    }
  }, [addToast]);

  useEffect(() => {
    fetchReports();
  }, [fetchReports]);

  /* Form handlers */
  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setForm((f) => ({
      ...f,
      [name]: name === 'photo' ? files[0] : value,
    }));
  };

  const handleMap = (lat, lng) => {
    setForm((f) => ({ ...f, latitude: lat, longitude: lng }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const fd = new FormData();
      Object.keys(form).forEach((key) => {
        if (form[key] !== null && form[key] !== '') {
          fd.append(key, form[key]);
        }
      });

      await api.post('/api/lost-reports', fd);
      fetchReports();
      addToast('Reporte enviado', 'success');
      setForm({
        type: 'lost', 
        pet_id: '',
        pet_name: '',
        comment: '',
        happened_at: '',
        latitude: null,
        longitude: null,
        photo: null,
      });
    } catch (error) {
      addToast('Error al enviar el reporte', 'error');
      console.error(error.response?.data || error);
    }
  };

  return (
    <div>
      <h1>Reportar Mascota Perdida o Encontrada</h1>

      <form onSubmit={handleSubmit} className="card p-4 mb-4">
        <select name="type" value={form.type} onChange={handleChange} className="form-select mb-3">
          <option value="lost">Reportar Mascota Perdida</option>
          <option value="found">Reportar Mascota Encontrada</option>
        </select>

        {form.type === 'lost' ? (
          <select
            name="pet_id"
            value={form.pet_id}
            onChange={handleChange}
            className="form-select mb-3"
            required
          >
            <option value="">Selecciona tu mascota</option>
            {/* Aquí debes cargar las mascotas del usuario */}
            <option value="1">Mascota 1</option>
            <option value="2">Mascota 2</option>
          </select>
        ) : (
          <input
            name="pet_name"
            className="form-control mb-2"
            placeholder="Nombre de la mascota encontrada"
            value={form.pet_name}
            onChange={handleChange}
            required={form.type === 'found'}
          />
        )}

        <textarea
          name="comment"
          className="form-control mb-3"
          placeholder="Descripción"
          value={form.comment}
          onChange={handleChange}
          required
        />

        <input
          type="datetime-local"
          name="happened_at"
          className="form-control mb-3"
          value={form.happened_at}
          onChange={handleChange}
          required
        />

        <MapPicker
          latitude={form.latitude}
          longitude={form.longitude}
          onChange={handleMap}
        />

        <input
          type="file"
          name="photo"
          className="form-control mt-3"
          onChange={handleChange}
        />

        <button className="btn btn-danger mt-3">Reportar</button>
      </form>
    </div>
  );
}
